// components/Navbar.jsx
import React, { useState, useEffect } from 'react';
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

const sections = [
  { label: 'Hakkımda', id: 'about' },
  { label: 'Eğitim', id: 'education' },
  { label: 'Deneyim', id: 'experience' },
  { label: 'Projeler', id: 'projects' },
  { label: 'Yetenekler', id: 'skills' },
  { label: 'İletişim', id: 'contact' },
];

const Navbar = () => {
  const [elevated, setElevated] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);

  const handleScroll = () => {
    setElevated(window.scrollY > 10);
  };

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setDrawerOpen(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <>
      <AppBar
        position="fixed"
        elevation={0}
        sx={{
        background: 'white',
          backdropFilter: elevated ? 'blur(10px)' : 'none',
          transition: 'all 0.3s ease',
          color: 'black',
        }}
      >
        <Toolbar
          sx={{
            justifyContent: 'center',
            px: { xs: 2, md: 10 },
            gap: 4,
          }}
        >
          {/* Masaüstü görünüm - içerikler ortalanmış */}
          <Box
            sx={{
              display: { xs: 'none', md: 'flex' },
              gap: 8,
              justifyContent: 'center',
              width: '100%',
            }}
          >
            {sections.map((section) => (
              <Typography
                key={section.id}
                onClick={() => scrollToSection(section.id)}
                sx={{ cursor: 'pointer', color: 'black', userSelect: 'none' }}
              >
                {section.label}
              </Typography>
            ))}
          </Box>

          {/* Mobil görünüm - hamburger menü */}
          <Box sx={{ display: { xs: 'block', md: 'none' }, position: 'absolute', right: 16 }}>
            <IconButton
              onClick={() => setDrawerOpen(true)}
              sx={{
                backgroundColor: 'transparent !important',
                color: 'black',
              }}
              aria-label="menu"
            >
              <MenuIcon />
            </IconButton>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Mobil menü drawer */}
      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        PaperProps={{
          sx: {
            backgroundColor: '#CDC1FF',
            color: 'black',
          },
        }}
      >
        <List>
          {sections.map((section) => (
            <ListItem
              button
              key={section.id}
              onClick={() => scrollToSection(section.id)}
              sx={{ color: 'black' }}
            >
              <ListItemText primary={section.label} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </>
  );
};

export default Navbar;
