import React, { useState, useEffect } from 'react';
import Grid from '@mui/material';
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

const sections = [
  { label: 'Hakkımda', id: 'about' },
  { label: 'Eğitim', id: 'education' },
  { label: 'Deneyim', id: 'experience' },
  { label: 'Projeler', id: 'projects' },
  { label: 'Yetenekler', id: 'skills' },
  { label: 'İletişim', id: 'contact' },
];

const Navbar = () => {
  const [elevated, setElevated] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => setElevated(window.scrollY > 10);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setDrawerOpen(false);
    }
  };

  return (
    <Box sx={{ minHeight: '70vh', p: 4, bgcolor: 'black', color: 'white' }}>
      <Grid container spacing={4} alignItems="center">

        {/* Sol üst: Büyük isim */}
        <Grid item xs={12} md={4} sx={{ textAlign: 'left' }}>
          <Typography variant="h2" fontWeight="bold" sx={{ lineHeight: 1.1 }}>
            Daniel <br /> Gallego
          </Typography>
        </Grid>

        {/* Sağ üst: İki fotoğraf */}
        <Grid item xs={12} md={8} sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
          <Box component="img" src="/path/to/photo1.jpg" alt="Fotoğraf 1" sx={{ width: '45%', height: 'auto' }} />
          <Box component="img" src="/path/to/photo2.jpg" alt="Fotoğraf 2" sx={{ width: '45%', height: 'auto' }} />
        </Grid>

        {/* Alt yatay çizgi, tüm grid boyunca ama ortalanmış ve genişliği kısıtlı */}
        <Grid item xs={12}>
          <Box
            sx={{
              borderBottom: '1px solid white',
              width: '80%',
              mx: 'auto',
              opacity: 0.3,
              mt: 2,
              mb: 2,
            }}
          />
        </Grid>

        {/* Alt metin alanı: Sol ve Sağ */}
        <Grid item xs={12} md={4} sx={{ textAlign: 'left', letterSpacing: 2, fontSize: '0.8rem' }}>
          CREATIVE PORTFOLIO
        </Grid>

        <Grid item xs={12} md={8} sx={{ textAlign: 'right' }}>
          <Typography sx={{ maxWidth: 400, marginLeft: 'auto' }}>
            I'm a model who can do both masculine and androgynous concepts. I feel myself come alive in front of the camera.
          </Typography>
        </Grid>
      </Grid>
    </Box>
  

  );
};

export default Navbar;
