import React from 'react';
import { Box, Typography, Grid, Paper } from '@mui/material';
import BuildIcon from '@mui/icons-material/Build';
import DesignServicesIcon from '@mui/icons-material/DesignServices';
import CodeIcon from '@mui/icons-material/Code';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';

const services = [
  {
    icon: <BuildIcon sx={{ fontSize: 40, color: '#90caf9' }} />, // Açık mavi
    title: 'Web Geliştirme',
    description: 'Modern ve duyarlı web uygulamaları geliştiriyorum.',
  },
  {
    icon: <DesignServicesIcon sx={{ fontSize: 40, color: '#90caf9' }} />,
    title: 'UI/UX Tasarım',
    description: 'Kullanıcı dostu ve şık tasarımlar oluşturuyorum.',
  },
  {
    icon: <CodeIcon sx={{ fontSize: 40, color: '#90caf9' }} />,
    title: 'Yazılım Danışmanlığı',
    description: 'Proje ihtiyaçlarınıza özel çözümler sunuyorum.',
  },
  {
    icon: <SupportAgentIcon sx={{ fontSize: 40, color: '#90caf9' }} />,
    title: 'Teknik Destek',
    description: 'Sorunlarınızı hızlı ve etkili şekilde çözüyorum.',
  },
];

const Services = () => {
  return (
    <Box sx={{ maxWidth: 900, mx: 'auto', mt: 6, px: 2 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom align="center">
        Hizmetler
      </Typography>
      <Typography
        variant="body1"
        align="center"
        color="text.secondary"
        mb={4}
      >
        Profesyonel olarak verdiğim hizmetlere aşağıdan ulaşabilirsiniz
      </Typography>
      <Grid container spacing={3}>
        {services.map(({ icon, title, description }, index) => (
          <Grid item xs={12} sm={6} key={index}>
            <Paper
              elevation={3}
              sx={{
                bgcolor: '#f5f5f5', // kırık beyaz
                p: 3,
                textAlign: 'center',
                borderRadius: 2,
                minHeight: 200,
              }}
            >
              <Box sx={{ mb: 2 }}>{icon}</Box>
              <Typography variant="h6" fontWeight="bold" gutterBottom>
                {title}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {description}
              </Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Services;
