import React from 'react';
import { Box, Typography, Avatar, IconButton } from '@mui/material';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';
import InstagramIcon from '@mui/icons-material/Instagram';
import aboutData from './data/about';

const About = () => {
  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        bgcolor: '#e6f2f0',
        p: 4,
      }}
    >
      <Box
        sx={{
          display: 'flex',
          width: { xs: '100%', sm: '90%', md: '70%' },
          minHeight: '400px',
          boxShadow: 6,
          borderRadius: 4,
          overflow: 'hidden',
        }}
      >
        {/* Sol (yeşil + mavi soft gradyan) kısım */}
        <Box
          sx={{
            width: '30%',
            background: 'linear-gradient(to bottom, #7ed957, #28a745, #cceeff)',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            p: 2,
            position: 'relative',
          }}
        >
          <Avatar
            src="asd.jpg"
            alt="Yakup Şimşek"
            sx={{ width: 100, height: 100, mb: 2, border: '3px solid white' }}
          />
          <Typography variant="h4" sx={{ color: 'white', mb: 1, textAlign: 'center' }}>
            Yakup Şimşek
          </Typography>
          <Typography variant="subtitle1" sx={{ color: 'white', mb: 2, textAlign: 'center' }}>
            Eğitim Teknoloğu
          </Typography>
          <Box>
            <IconButton color="inherit"><LinkedInIcon sx={{ color: 'white' }} /></IconButton>
            <IconButton color="inherit"><GitHubIcon sx={{ color: 'white' }} /></IconButton>
            <IconButton color="inherit"><InstagramIcon sx={{ color: 'white' }} /></IconButton>
          </Box>
        </Box>

        {/* Sağ (beyaz) kısım */}
        <Box
          sx={{
            width: '70%',
            bgcolor: 'white',
            p: 4,
            borderTopLeftRadius: 40,
            borderBottomLeftRadius: 40,
            ml: '-30px',
            zIndex: 1,
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'center',
          }}
        >
          {aboutData.map((item, index) => (
            <Typography key={index} variant="body1" sx={{ color: '#333', mb: 2 }}>
              • {item}
            </Typography>
          ))}

          <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
            <MailOutlineIcon sx={{ color: '#001f4d', mr: 1 }} />
            <Box
              onClick={() => window.open('mailto:yakupsimseke@gmail.com')}
              sx={{
                display: 'flex',
                alignItems: 'center',
                gap: 1,
                px: 2,
                py: 1,
                bgcolor: 'white',
                borderRadius: 2,
                cursor: 'pointer',
                border: '2px solid #001f4d',
                color: 'black',
                transition: '0.3s',
                '&:hover': {
                  bgcolor: '#001f4d',
                  color: 'white',
                },
              }}
            >
              <Typography variant="body1" sx={{ fontWeight: 600 }}>
                Benimle İletişime Geçin
              </Typography>
              <ArrowForwardIosIcon sx={{ fontSize: 16 }} />
            </Box>
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default About;
