import React from 'react';
import { Box, Typography, Grid, Avatar } from '@mui/material';

const About = () => {
  return (
    <Box id="about" 
    sx={{
       minHeight: '60vh',
        display: 'flex',
        flexDirection: 'column',
        pt: 20,
        
       
       
         
        zIndex: 1,  
           }}>
      {/* Üst kısım: Sol ve sağ */}
      <Grid container spacing={4} alignItems="center" justifyContent="space-between">
       
        <Grid item xs={12} md={12}>
          <Typography variant="h3" fontWeight="bold"
           sx={{

          }}>
            Yakup <br />
            <Box component="span" sx={{ display: 'block', mt: 1 }}>
              ŞİMŞEK
            </Box>
          </Typography>
        </Grid>

        {/* Sağ taraf - İki yatay fotoğraf */}
        <Grid
          item
          xs={12}
          md={6}
          sx={{
            display: 'flex',
            justifyContent: 'flex-end',
            gap: 2,
          }}
        >
          <Avatar
            variant="rounded"
            src="https://via.placeholder.com/200x300"
            alt="Fotoğraf 1"
            sx={{ width: 150, height: 225 }}
          />
          <Avatar
            variant="rounded"
            src="https://via.placeholder.com/200x300"
            alt="Fotoğraf 2"
            sx={{ width: 150, height: 225 }}
          />
        </Grid>
      </Grid>

      {/* Altında yatay kesik çizgi */}
      <Box
        sx={{
          width: 150,
          height: 3,
          bgcolor: 'primary.main',
          mx: 'auto',
          my: 5,
          borderRadius: 2,
          opacity: 0.7,
        }}
      />

      {/* Alt bölüm - Sol ve Sağ */}
      <Grid container spacing={4} alignItems="flex-start" justifyContent="space-between">
        {/* Sol taraf - Eğitim Teknoloğu */}
        <Grid item xs={12} md={30} sx={{ textAlign: 'left' }}>
          <Typography variant="h6" fontWeight="bold">
            Eğitim Teknoloğu
          </Typography>
        </Grid>

        {/* Sağ taraf - Açıklama metni */}
        <Grid item xs={12} md={7} sx={{ textAlign: 'right' }}>
          <Typography sx={{ whiteSpace: 'pre-line', fontSize: '1rem', lineHeight: 1.6 }}>
            Teknolojinin eğitimi dönüştürme gücüne inanarak, öğrencilere ve eğitimcilere yenilikçi çözümler sunmayı
            hedefliyorum. Üniversite eğitimim boyunca çeşitli projelerde yer alarak eğitimde dijital dönüşüm üzerine
            çalışmalar yaptım. Özellikle öğrenme yönetim sistemlerinin (LMS) kullanımı, interaktif içerik geliştirme
            ve veri analitiği ile öğrenme süreçlerini iyileştirme konularında kendimi geliştirdim. Ekip çalışmasına büyük
            değer verir, eleştirilere açık bir şekilde sürekli öğrenme arzusu içindeyim. Yaratıcılığım, metinleri hayata
            geçirme, hikayeler anlatma ve izleyici üzerinde etkili olma yeteneğimle öne çıkarım. Her proje bana yeni
            bir hikaye anlatma fırsatı sunar ve bu da beni sürekli motive eder.
          </Typography>
        </Grid>
      </Grid>
    </Box>
  );
};

export default About;
