import React from 'react';
import { Box, Typography, Paper } from '@mui/material';
import BuildIcon from '@mui/icons-material/Build';
import DesignServicesIcon from '@mui/icons-material/DesignServices';
import CodeIcon from '@mui/icons-material/Code';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';

const Services = () => {
  const servicesData = [
    {
      icon: <BuildIcon sx={{ fontSize: 70, color: '#90caf9' }} />,
      title: 'Web Geliştirme',
      desc: 'Modern ve duyarlı web uygulamaları geliştiriyorum.',
    },
    {
      icon: <DesignServicesIcon sx={{ fontSize: 70, color: '#90caf9' }} />,
      title: 'UI/UX Tasarım',
      desc: 'Kullanıcı dostu ve şık tasarımlar oluşturuyorum.',
    },
    {
      icon: <CodeIcon sx={{ fontSize: 70, color: '#90caf9' }} />,
      title: 'Yazılım Danışmanlığı',
      desc: 'Proje ihtiyaçlarınıza özel çözümler sunuyorum.',
    },
    {
      icon: <SupportAgentIcon sx={{ fontSize: 70, color: '#90caf9' }} />,
      title: 'Teknik Destek',
      desc: 'Sorunlarınızı hızlı ve etkili şekilde çözüyorum.',
    },
  ];

  return (
    <Box sx={{ maxWidth: '100%', mx: 'auto', mt: 6, px: 0 }}>
      <Typography
        variant="h4"
        fontWeight="bold"
        gutterBottom
        align="center"
        sx={{ px: 2 }}
      >
        Hizmetler
      </Typography>
      <Typography
        variant="body1"
        align="center"
        color="text.secondary"
        mb={6}
        maxWidth={700}
        mx="auto"
        sx={{ px: 2 }}
      >
        Profesyonel olarak verdiğim hizmetlere aşağıdan ulaşabilirsiniz
      </Typography>

      <Box
        sx={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          gap: 3,
          px: 1,
        }}
      >
        {servicesData.map(({ icon, title, desc }, index) => (
          <Paper
            key={index}
            elevation={0}
            sx={{
              border: '2px solid #90caf9',
              bgcolor: 'transparent',
              p: 5,
              borderRadius: 3,
              display: 'flex',
              alignItems: 'center',
              gap: 3,
              flexDirection: 'row',
              flexBasis: 'calc(50% - 24px)', // 2 sütun için %50 - gap
              maxWidth: 'calc(50% - 24px)',
              minHeight: 500,
              transition: 'transform 0.3s ease',
              '&:hover': {
                transform: 'scale(1.05)',
                borderColor: '#64b5f6',
                boxShadow: '0 0 15px #90caf9',
              },
              '@media (max-width:600px)': {
                flexBasis: '100%',
                maxWidth: '100%',
                minHeight: 400,
              },
            }}
          >
            {icon}
            <Box>
              <Typography variant="h5" fontWeight="bold" gutterBottom>
                {title}
              </Typography>
              <Typography variant="body1" color="text.secondary">
                {desc}
              </Typography>
            </Box>
          </Paper>
        ))}
      </Box>
    </Box>
  );
};

export default Services;
