import React, { useState, useEffect } from 'react';
import {
  Box, Typography, Grid, Paper, IconButton,
  TextField, Button
} from '@mui/material';
import WorkIcon from '@mui/icons-material/Work';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';

import axios from 'axios';
import {jwtDecode} from "jwt-decode";

const Project = () => {
  const [data, setData] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [title, setTitle] = useState("");
  const [descriptions, setDescriptions] = useState([""]);
  const [showForm, setShowForm] = useState(false);

  // Düzenleme için state
  const [editIndex, setEditIndex] = useState(null);
  const [editTitle, setEditTitle] = useState("");
  const [editDescriptions, setEditDescriptions] = useState([""]);

  useEffect(() => {
    // Admin kontrolü
    const token = localStorage.getItem("token") || sessionStorage.getItem("token");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setIsAdmin(decoded.isAdmin || false);
      } catch {
        setIsAdmin(false);
      }
    }

    // Veri çekme
    const fetchData = async () => {
      try {
        const res = await axios.get("http://localhost:3003/project/getText");
        const formatted = res.data.map(item => ({
          ...item,
          description: typeof item.description === 'string' ? JSON.parse(item.description) : item.description,
        }));
        setData(formatted);
      } catch (error) {
        console.error("Veri çekme hatası", error);
        setError("Veri alınırken hata oluştu.");
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleNext = () => {
    setActiveIndex((prev) => (prev + 1) % data.length);
    setEditIndex(null);
  };

  const handlePrev = () => {
    setActiveIndex((prev) => (prev - 1 + data.length) % data.length);
    setEditIndex(null);
  };

  const handleAddDescription = () => {
    setDescriptions(prev => [...prev, ""]);
  };

  const handleDescriptionChange = (index, value) => {
    const updated = [...descriptions];
    updated[index] = value;
    setDescriptions(updated);
  };

  const handleSubmit = async () => {
    if (!title.trim() || descriptions.some(desc => !desc.trim())) {
      alert("Başlık ve açıklamaları doldurun.");
      return;
    }

    const token = localStorage.getItem("token") || sessionStorage.getItem("token");
    try {
      const res = await axios.post("http://localhost:3003/project/addText", {
        title,
        description: descriptions,
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      const newProject = {
        ...res.data,
        description: descriptions,
      };

      setData(prev => [...prev, newProject]);
      setTitle("");
      setDescriptions([""]);
      setShowForm(false);
    } catch (err) {
      console.error("Ekleme hatası:", err);
      alert("Ekleme sırasında hata oluştu.");
    }
  };

  // Düzenleme işlemleri
  const startEdit = (index) => {
    setEditIndex(index);
    setEditTitle(data[index].title);
    setEditDescriptions(data[index].description);
  };

  const handleEditDescriptionChange = (index, value) => {
    const updated = [...editDescriptions];
    updated[index] = value;
    setEditDescriptions(updated);
  };

  const handleAddEditDescription = () => {
    setEditDescriptions(prev => [...prev, ""]);
  };

  const handleCancelEdit = () => {
    setEditIndex(null);
  };

  const handleSaveEdit = async () => {
    if (!editTitle.trim() || editDescriptions.some(desc => !desc.trim())) {
      alert("Başlık ve açıklamaları doldurun.");
      return;
    }

    const token = localStorage.getItem("token") || sessionStorage.getItem("token");
    try {
      await axios.put(`http://localhost:3003/project/updateText/${data[editIndex].id}`, {
        title: editTitle,
        description: editDescriptions,
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      setData(prev => {
        const newData = [...prev];
        newData[editIndex] = {
          ...newData[editIndex],
          title: editTitle,
          description: editDescriptions,
        };
        return newData;
      });

      setEditIndex(null);
    } catch (err) {
      console.error("Güncelleme hatası:", err);
      alert("Güncelleme sırasında hata oluştu.");
    }
  };

  if (loading) return <Typography align="center">Yükleniyor...</Typography>;
  if (error) return <Typography align="center" color="error">{error}</Typography>;
  if (!data.length) return <Typography align="center">Hiç proje bulunamadı.</Typography>;

  return (
    <Box
      id="experience"
      sx={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        pt: { xs: 4, md: 8 },
        px: 2,
      }}
    >
      <Typography color="white">Admin durumu: {isAdmin ? "Evet" : "Hayır"}</Typography>
      <Typography
        variant='h3'
        sx={{
          fontFamily: '"Segoe UI", sans-serif',
          fontWeight: 'bold',
          color: 'white',
          mb: 6,
          textAlign: 'center',
          fontSize: { xs: 16, sm: 18, md: 20, lg: 24, xl: 44 },
        }}
      >
        Projeler
      </Typography>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          gap: 2,
          flexDirection: { xs: 'column', sm: 'row' },
        }}
      >
        <IconButton onClick={handlePrev}>
          <ChevronLeftIcon sx={{ fontSize: 48, color: '#E2E2B6' }} />
        </IconButton>

        <Paper
          elevation={6}
          sx={{
            width: '80%',
            maxWidth: { xs: '90%', sm: '95%', md: 800 },
            p: { xs: 3, md: 5 },
            borderRadius: 4,
            background: 'linear-gradient(100deg, #4583cf, #03346E )',
            color: '#EAE0C8',
            transition: 'all 0.5s ease',
            minHeight: 300,
            position: 'relative',
          }}
        >

          <Grid container spacing={2} justifyContent="center">
            <Grid item xs={12} sm={6} display="flex" alignItems="center">
              <WorkIcon sx={{ mr: 1, fontSize: 28 }} />
              <Typography variant="h6" fontWeight="bold">
                {data[activeIndex].title}
              </Typography>
            </Grid>
          </Grid>

          <Box mt={3} sx={{ pt: 8, textAlign: 'center' }}>
            <ul style={{ paddingLeft: '1.2rem', margin: 0 }}>
              {data[activeIndex].description.map((item, i) => (
                <li key={i} style={{ marginBottom: '12px' }}>
                  <Typography variant="body1" sx={{
                    fontSize: { xs: 16, sm: 18, md: 18, lg: 18, xl: 20 }
                  }}>
                    {item}
                  </Typography>
                </li>
              ))}
            </ul>
          </Box>

          {/* Düzenle butonu - sadece admin ise ve düzenleme formu kapalıysa */}
          {isAdmin && editIndex !== activeIndex && (
            <Box textAlign="center" mt={2}>
              <Button
                variant="outlined"
                onClick={() => startEdit(activeIndex)}
                sx={{ color: 'white', borderColor: 'white' }}
              >
                Düzenle
              </Button>
            </Box>
          )}

          {/* Düzenleme formu aktifse */}
          {editIndex === activeIndex && (
            <Box
              mt={3}
              p={3}
              sx={{
                backgroundColor: '#fff',
                borderRadius: 2,
                color: '#000',
                textAlign: 'left',
              }}
            >
              <TextField
                fullWidth
                label="Başlık"
                value={editTitle}
                onChange={e => setEditTitle(e.target.value)}
                sx={{ mb: 2 }}
              />
              {editDescriptions.map((desc, i) => (
                <TextField
                  key={i}
                  fullWidth
                  multiline
                  label={`Açıklama ${i + 1}`}
                  value={desc}
                  onChange={e => handleEditDescriptionChange(i, e.target.value)}
                  sx={{ mb: 2 }}
                />
              ))}
              <Button onClick={handleAddEditDescription} variant="outlined" sx={{ mb: 2, mr: 2 }}>
                Açıklama Ekle
              </Button>
              <Box>
                <Button variant="contained" onClick={handleSaveEdit} sx={{ mr: 2 }}>
                  Kaydet
                </Button>
                <Button variant="outlined" onClick={handleCancelEdit} color="error">
                  İptal
                </Button>
              </Box>
            </Box>
          )}
        </Paper>

        <IconButton onClick={handleNext}>
          <ChevronRightIcon sx={{ fontSize: 48, color: '#E2E2B6' }} />
        </IconButton>
      </Box>

      {isAdmin && !showForm && (
        <Button
          variant="contained"
          color="primary"
          onClick={() => setShowForm(true)}
          sx={{ mt: 6 }}
        >
          + Yeni Proje Ekle
        </Button>
      )}

      {isAdmin && showForm && (
        <Box
          mt={6}
          p={3}
          sx={{
            backgroundColor: '#1b1b1b',
            borderRadius: 4,
            maxWidth: 600,
            width: '100%',
          }}
        >
          <Typography variant="h6" color="white" mb={3}>
            Yeni Proje Ekle
          </Typography>
          <TextField
            fullWidth
            label="Başlık"
            value={title}
            onChange={e => setTitle(e.target.value)}
            sx={{ mb: 2 }}
            InputLabelProps={{ style: { color: '#ccc' } }}
            inputProps={{ style: { color: 'white' } }}
          />
          {descriptions.map((desc, i) => (
            <TextField
              key={i}
              fullWidth
              multiline
              label={`Açıklama ${i + 1}`}
              value={desc}
              onChange={e => handleDescriptionChange(i, e.target.value)}
              sx={{ mb: 2 }}
              InputLabelProps={{ style: { color: '#ccc' } }}
              inputProps={{ style: { color: 'white' } }}
            />
          ))}
          <Button variant="outlined" onClick={handleAddDescription} sx={{ mb: 2 }}>
            Açıklama Ekle
          </Button>
          <Box>
            <Button
              variant="contained"
              color="primary"
              onClick={handleSubmit}
              sx={{ mr: 2 }}
            >
              Kaydet
            </Button>
            <Button
              variant="outlined"
              color="error"
              onClick={() => setShowForm(false)}
            >
              İptal
            </Button>
          </Box>
        </Box>
      )}
    </Box>
  );
};

export default Project;
