import React, { useEffect, useState } from 'react';
import { Box, Typography, Avatar, IconButton } from '@mui/material';
import axios from 'axios';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';
import InstagramIcon from '@mui/icons-material/Instagram';
// import aboutData from '../data/about';



// useEffect(() => {
//   axios.get('http://localhost:3003/about/getText')
//   .then(response => {
//     const texts = response.data.map(item=> item.text);
//     setAboutData(texts);
//   })
//   .catch(error => {
//     console.error('veriyi getiremedik ağabeyy',error);
//   });
// }, []);

const neonIconStyle = {
  color: 'white',
  transition: 'transform 0.3s ease, filter 0.3s ease',
  filter: 'drop-shadow(0 0 6px rgb(0, 0, 0)) drop-shadow(0 0 10px rgb(61, 14, 148))',
  '&:hover': {
    transform: 'scale(1.3)',
    filter: 'drop-shadow(0 0 12px rgb(255, 255, 255)) drop-shadow(0 0 20px #ffbbff)',
  },
};



const About = () => {
  const [aboutData, setAboutData] = useState([]);

  useEffect(() => {
  axios.get('http://localhost:3003/about/getText')
  .then(response => {
    const texts = response.data.map(item=> item.text);
    setAboutData(texts);
  })
  .catch(error => {
    console.error('veriyi getiremedik ağabeyy',error);
  });
}, []);



  return (
    <Box
      id="about"
      sx={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        p: 2,
        overflowY: 'auto',
      }}
    >
      <Box
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', md: 'row' },
          width: { xs: '100%', sm: '90%', md: '70%' },
          boxShadow: 6,
          borderRadius: 4,
          overflow: 'hidden',
        }}
      >
        {/* Sol Kısım */}
        <Box
          sx={{
            width: { xs: '90%', md: '30%' },
            background: 'linear-gradient(to bottom,rgb(27, 186, 214),rgb(12, 150, 192),rgb(60, 48, 218))',
            display: 'flex',
            flexDirection: 'column',
            alignItems:'center',
            justifyContent: 'center',
            p: 3,
            
          }}
        >
           <Avatar
              src="asd.jpg"
              alt="Yakup Şimşek"
              sx={{
                width: { xs: 120, sm: 160, md: 200 },
                height: { xs: 120, sm: 160, md: 200 },
                mb: 2,
                border: '3px solid white',
                maxWidth: '100%',
              }}
            />
          <Typography variant="h4" sx={{ color: 'white', mb: 1, textAlign: 'center' }}>
            Yakup Şimşek
          </Typography>
          <Typography variant="h6" sx={{ color: 'white', mb: 2, textAlign: 'center' }}>
            Eğitim Teknoloğu
          </Typography>
          <Box>
              <IconButton
                    sx={{
                      transition: 'all 0.3s ease',
                      '&:hover svg': {
                        transform: 'scale(1.3)',
                        filter: 'drop-shadow(0 0 32px rgb(0, 0, 0)) drop-shadow(0 0 20px rgb(255, 255, 255))',
                      },
                    }}
                  >
                    <LinkedInIcon sx={{ fontSize: 30, ...neonIconStyle }} />
              </IconButton>

              <IconButton
                    sx={{
                      transition: 'all 0.3s ease',
                      '&:hover svg': {
                        transform: 'scale(1.3)',
                        filter: 'drop-shadow(0 0 32px rgb(0, 0, 0)) drop-shadow(0 0 20px rgb(255, 255, 255))',
                      },
                    }}
                  >
                    <GitHubIcon sx={{ fontSize: 30, ...neonIconStyle }} />
              </IconButton>

              <IconButton
                    sx={{
                      transition: 'all 0.3s ease',
                      '&:hover svg': {
                        transform: 'scale(1.3)',
                        filter: 'drop-shadow(0 0 32px rgb(0, 0, 0)) drop-shadow(0 0 20px rgb(255, 255, 255))',
                      },
                    }}
                  >
                    <InstagramIcon sx={{ fontSize: 30, ...neonIconStyle }} />
              </IconButton>


          </Box>
        </Box>

        {/* Sağ Kısım */}
        <Box sx={{ p: 2 }}>
      <Typography variant="h4" align="center" gutterBottom>
        Hakkımda
      </Typography>

      {aboutData.map(item => (
        <Box key={item.id} sx={{ mb: 2, p: 2, border: '1px solid #ccc', borderRadius: 2, position: 'relative' }}>
          <Typography>{item.text}</Typography>
          {isAdmin && (
            <Box sx={{ position: 'absolute', top: 8, right: 8 }}>
              <Button variant="outlined" size="small" onClick={() => handleEditOpen(item)} sx={{ mr: 1 }}>
                Düzenle
              </Button>
              <Button variant="outlined" color="error" size="small" onClick={() => handleDeleteOpen(item)}>
                Sil
              </Button>
            </Box>
          )}
        </Box>
      ))}

      {/* Düzenleme Dialogu */}
      <Dialog open={editOpen} onClose={handleEditClose}>
        <DialogTitle>Metni Düzenle</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Metin"
            type="text"
            fullWidth
            multiline
            minRows={3}
            value={currentEdit.text}
            onChange={(e) => setCurrentEdit(prev => ({ ...prev, text: e.target.value }))}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleEditClose}>İptal</Button>
          <Button onClick={handleEditSave} variant="contained">Kaydet</Button>
        </DialogActions>
      </Dialog>

      {/* Silme Onay Dialogu */}
      <Dialog open={deleteOpen} onClose={handleDeleteClose}>
        <DialogTitle>Metni Sil</DialogTitle>
        <DialogContent>
          <DialogContentText>
            "{currentEdit.text}" metnini silmek istediğinize emin misiniz?
          </DialogContentText>
          <FormControlLabel
            control={<Checkbox checked={confirmDelete} onChange={(e) => setConfirmDelete(e.target.checked)} />}
            label="Evet, silmek istediğimi onaylıyorum"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleDeleteClose}>İptal</Button>
          <Button onClick={handleDeleteConfirm} variant="contained" color="error">Sil</Button>
        </DialogActions>
      </Dialog>
    </Box>
          </Box>
        </Box>
  );
};
     

export default About;
