// components/Navbar.jsx
import React, { useState, useEffect } from 'react';
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

const sections = [
  { label: 'Hakkımda', id: 'about' },
  { label: 'Eğitim', id: 'education' },
  { label: 'Deneyim', id: 'experience' },
  { label: 'Projeler', id: 'projects' },
  { label: 'Yetenekler', id: 'skills' },
  { label: 'İletişim', id: 'contact' },
];

const Navbar = () => {
  const [elevated, setElevated] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);

  const handleScroll = () => {
    setElevated(window.scrollY > 10);
  };

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setDrawerOpen(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <>
      <AppBar
  position="fixed"
  elevation={0}
  sx={{
    backgroundColor: elevated ? 'transparent' : 'white',
    backdropFilter: elevated ? 'none' : 'blur(10px)',
    transition: 'all 0.3s ease',
  }}
>
  <Toolbar sx={{ justifyContent: 'center' }}>
    
    <Box
      sx={{
        display: { xs: 'none', md: 'flex' },
        gap: 8,
        justifyContent: 'center',
        width: '100%',
      }}
    >
      {sections.map((section) => (
        <Typography
          key={section.id}
          onClick={() => scrollToSection(section.id)}
          sx={{
            cursor: 'pointer',
            userSelect: 'none',
            color: elevated ? 'white' : 'black', // Burada yazı rengi
            transition: 'color 0.3s ease',
          }}
        >
          {section.label}
        </Typography>
      ))}
    </Box>

    
    

  </Toolbar>
</AppBar>


      {/* Mobil menü drawer */}
      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        PaperProps={{
          sx: {
            backgroundColor: '#CDC1FF',
            color: 'black',
          },
        }}
      >
        <List>
          {sections.map((section) => (
            <ListItem
              button
              key={section.id}
              onClick={() => scrollToSection(section.id)}
              sx={{ color: 'black' }}
            >
              <ListItemText primary={section.label} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </>
  );
};

export default Navbar;
