import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Grid,
  Paper,
  IconButton,
  Button,
  CircularProgress,
} from '@mui/material';
import WorkIcon from '@mui/icons-material/Work';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleIcon from '@mui/icons-material/AddCircle';
import axios from 'axios';

const Project = () => {
  const [data, setData] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const [loading, setLoading] = useState(false);

  // Admin kontrolü
  const token = sessionStorage.getItem('jwtDevode') || '';
  // Basit örnek: token içinde "admin" geçiyor mu diye kontrol
  const isAdmin = token.toLowerCase().includes('admin');

  // Projeleri API'den çek
  const fetchProjects = async () => {
    setLoading(true);
    try {
      const res = await axios.get('http://localhost:3003/project/getText');
      setData(res.data);
      setActiveIndex(0);
    } catch (error) {
      alert('Projeler yüklenirken hata oluştu.');
      console.error(error);
    }
    setLoading(false);
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const handleNext = () => {
    setActiveIndex((prev) => (prev + 1) % data.length);
  };

  const handlePrev = () => {
    setActiveIndex((prev) => (prev - 1 + data.length) % data.length);
  };

  // Add new project
  const handleAdd = async () => {
    const title = prompt('Yeni projenin başlığını girin:');
    if (!title) return alert('Başlık boş olamaz.');

    const descriptionText = prompt(
      'Proje açıklamalarını, maddeler halinde, noktalı virgül (;) ile ayırarak yazın:'
    );
    if (!descriptionText) return alert('Açıklama boş olamaz.');

    const description = descriptionText.split(';').map((d) => d.trim()).filter(Boolean);

    try {
      await axios.post(
        'http://localhost:3003/project/addText',
        { title, description },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Proje başarıyla eklendi.');
      fetchProjects();
    } catch (error) {
      alert('Proje eklenirken hata oluştu.');
      console.error(error);
    }
  };

  // Update project
  const handleUpdate = async () => {
    if (!data[activeIndex]) return;
    const current = data[activeIndex];

    const title = prompt('Projeyi güncelle - Başlık:', current.title);
    if (!title) return alert('Başlık boş olamaz.');

    const descriptionText = prompt(
      'Projeyi güncelle - Açıklamalar, noktalı virgül (;) ile ayırarak:',
      current.description.join('; ')
    );
    if (!descriptionText) return alert('Açıklama boş olamaz.');

    const description = descriptionText.split(';').map((d) => d.trim()).filter(Boolean);

    try {
      await axios.put(
        `http://localhost:3003/project/updateText/${current.id}`,
        { title, description },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Proje başarıyla güncellendi.');
      fetchProjects();
    } catch (error) {
      alert('Proje güncellenirken hata oluştu.');
      console.error(error);
    }
  };

  // Delete project
  const handleDelete = async () => {
    if (!data[activeIndex]) return;
    const current = data[activeIndex];

    if (!window.confirm(`"${current.title}" projesini silmek istediğinize emin misiniz?`)) return;

    try {
      await axios.delete(`http://localhost:3003/project/deleteText/${current.id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Proje başarıyla silindi.');
      fetchProjects();
    } catch (error) {
      alert('Proje silinirken hata oluştu.');
      console.error(error);
    }
  };

  if (loading) {
    return (
      <Box
        sx={{
          minHeight: '100vh',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          color: 'white',
        }}
      >
        <CircularProgress color="inherit" />
      </Box>
    );
  }

  if (data.length === 0) {
    return (
      <Box sx={{ color: 'white', textAlign: 'center', pt: 6 }}>
        <Typography variant="h6">Gösterilecek proje bulunamadı.</Typography>
        {isAdmin && (
          <Button
            variant="contained"
            color="primary"
            startIcon={<AddCircleIcon />}
            onClick={handleAdd}
            sx={{ mt: 2 }}
          >
            Proje Ekle
          </Button>
        )}
      </Box>
    );
  }

  return (
    <Box
      id="experience"
      sx={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        pt: { xs: 4, md: 8 },
        px: 2,
      }}
    >
      <Typography
        variant="h3"
        sx={{
          fontFamily: '"Segoe UI", sans-serif',
          fontWeight: 'bold',
          color: 'white',
          mb: 6,
          textAlign: 'center',
          fontSize: { xs: 16, sm: 18, md: 20, lg: 24, xl: 44 },
        }}
      >
        Projeler
      </Typography>

      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          gap: 2,
          flexDirection: { xs: 'column', sm: 'row' },
          width: '100%',
          maxWidth: 900,
        }}
      >
        <IconButton onClick={handlePrev}>
          <ChevronLeftIcon sx={{ fontSize: 48, color: '#E2E2B6' }} />
        </IconButton>

        <Paper
          elevation={6}
          sx={{
            width: '80%',
            maxWidth: { xs: '90%', sm: '95%', md: 800 },
            p: { xs: 3, md: 5 },
            borderRadius: 4,
            backgroundColor: 'white',
            color: 'black',
            transition: 'all 0.5s ease',
            minHeight: 300,
            position: 'relative',
          }}
        >
          <Grid container spacing={2} justifyContent="center" alignItems="center">
            <Grid item xs={12} sm={6} display="flex" alignItems="center">
              <WorkIcon sx={{ mr: 1, fontSize: 28 }} />
              <Typography variant="h6" fontWeight="bold">
                {data[activeIndex].title}
              </Typography>
            </Grid>

            {isAdmin && (
              <Grid
                item
                xs={12}
                sm={6}
                display="flex"
                justifyContent="flex-end"
                gap={1}
                sx={{ pr: 2 }}
              >
                <Button
                  variant="contained"
                  size="small"
                  color="primary"
                  startIcon={<EditIcon />}
                  onClick={handleUpdate}
                >
                  Düzenle
                </Button>
                <Button
                  variant="contained"
                  size="small"
                  color="error"
                  startIcon={<DeleteIcon />}
                  onClick={handleDelete}
                >
                  Sil
                </Button>
              </Grid>
            )}
          </Grid>

          <Box
            mt={3}
            sx={{
              pt: 8,
              textAlign: 'center',
            }}
          >
            <ul style={{ paddingLeft: '1.2rem', margin: 0 }}>
              {data[activeIndex].description.map((item, i) => (
                <li key={i} style={{ marginBottom: '12px' }}>
                  <Typography
                    variant="body1"
                    sx={{
                      fontSize: { xs: 16, sm: 18, md: 18, lg: 18, xl: 20 },
                    }}
                  >
                    {item}
                  </Typography>
                </li>
              ))}
            </ul>
          </Box>
        </Paper>

        <IconButton onClick={handleNext}>
          <ChevronRightIcon sx={{ fontSize: 48, color: '#E2E2B6' }} />
        </IconButton>
      </Box>

      {isAdmin && (
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddCircleIcon />}
          sx={{ mt: 6 }}
          onClick={handleAdd}
        >
          Proje Ekle
        </Button>
      )}
    </Box>
  );
};

export default Project;
