import React, { useEffect, useState } from 'react';
import { Box, Typography, Grid } from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';

const Education = () => {
  const [gpa, setGpa] = useState(0);

  // GPA animasyonu
  useEffect(() => {
    let start = 0;
    const end = 3.10;
    const duration = 3000; // ms
    const increment = end / (duration / 50);
    const timer = setInterval(() => {
      start += increment;
      if (start >= end) {
        start = end;
        clearInterval(timer);
      }
      setGpa(start.toFixed(2));
    }, 50);

    return () => clearInterval(timer);
  }, []);

  return (
    <Box
  id="education"
  sx={{
    height: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    p: 2,
  }}
>
  <Box
    sx={{
      bgcolor: 'white',
      p: 4,
      borderRadius: 4,
      boxShadow: 6,
      width: { xs: '100%', sm: '90%', md: '70%', lg: '60%' },
      maxWidth: 600,
      maxHeight: 300,
      height: { xs: '100%', sm: '90%', md: '70%', lg: '40%' },
      transition:
        'transform 0.3s ease, box-shadow 0.3s ease, background-color 0.3s ease, color 0.3s ease',
      color: '#2D336B', // yazı rengi öncesi
      '& svg': {
        color: '#2D336B', // svg hover öncesi rengi
        transition: 'color 0.3s ease',
      },
      '&:hover': {
        transform: 'scale(1.02)',
        boxShadow: 10,
        bgcolor: '#2D336B', // arka plan hoverda
        color: 'white', // yazı hoverda beyaz
        '& svg': {
          color: 'white', // svg hoverda beyaz
        },
      },
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between', // Başlık ve içerik arası boşluk için
    }}
  >
    {/* Başlık */}
    <Typography
      variant="h4"
      sx={{
        textAlign: 'center',
        fontWeight: 'bold',
        mb: 3,
        color: 'inherit',
      }}
    >
      Eğitim
    </Typography>

    {/* İçerik */}
    <Grid container justifyContent="space-between" alignItems="center">
      <Grid item sx={{ display: 'flex', alignItems: 'center' }}>
        <SchoolIcon sx={{ mr: 1, color: 'inherit' }} />
        <Typography variant="h6">Hacettepe Üniversitesi</Typography>
      </Grid>

      <Grid item sx={{ display: 'flex', alignItems: 'center' }}>
        <CalendarMonthIcon sx={{ mr: 1, color: 'inherit' }} />
        <Typography variant="body1" sx={{ fontWeight: 'medium' }}>
          2020 - 2024
        </Typography>
      </Grid>
    </Grid>

    {/* Bölüm Adı */}
    <Box sx={{ mt: 3, textAlign: 'center' }}>
      <Typography
        variant="body1"
        sx={{ fontSize: { xs: 16, sm: 18 }, color: 'inherit' }}
      >
        Bilgisayar ve Öğretim Teknolojileri Eğitimi
      </Typography>
    </Box>

    {/* GPA kutusu - artık ana kutunun içinde */}
    <Box
      sx={{
        mt: 4,
        p: 1,
        bgcolor: '#7886C7',
        borderRadius: 2,
        maxWidth: 120,
        ml: 'auto',
        color: 'white',
        fontWeight: '600',
        textAlign: 'center',
        border: '2px solid #7886C7',
      }}
    >
      <Typography>GPA: 4.0 / {gpa}</Typography>
    </Box>
  </Box>
</Box>

  );
};

export default Education;
