import { useState, useEffect } from "react";
import {jwtDecode} from "jwt-decode";
import Swal from "sweetalert2";

import {
  Box,
  Typography,
  Paper,
  TextField,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
} from "@mui/material";
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
} from "@mui/lab";
import WorkspacesIcon from "@mui/icons-material/Workspaces";

const Experience = () => {
  const [experienceData, setExperienceData] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [editingExperience, setEditingExperience] = useState(null); // Düzenlenen deneyim
  const [newExperience, setNewExperience] = useState({
    title: "",
    description: [""],
  });

  // Silme onayı için dialog state
  const [deleteConfirmOpen, setDeleteConfirmOpen] = useState(false);
  const [experienceToDelete, setExperienceToDelete] = useState(null);

  // isAdmin kontrolü
  useEffect(() => {
    const token = sessionStorage.getItem("token");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setIsAdmin(decoded.isAdmin === true);
      } catch (err) {
        console.error("Token çözümlenemedi:", err);
      }
    }
  }, []);

  // Verileri çek
  useEffect(() => {
    fetchExperience();
  }, []);

  const fetchExperience = async () => {
    try {
      const response = await fetch("http://localhost:3003/experience/getText");
      const data = await response.json();
      setExperienceData(data);
    } catch (error) {
      console.error("Verileri getiremedim:", error);
    }
  };

  // Add or Update Experience
  const handleSaveExperience = async () => {
    const token = sessionStorage.getItem("token");
    const url = editingExperience
      ? `http://localhost:3003/experience/updateText/${editingExperience.id}`
      : "http://localhost:3003/experience/addText";
    const method = editingExperience ? "PUT" : "POST";

    try {
      const response = await fetch(url, {
        method,
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`, // token ekle
        },
        body: JSON.stringify({
          title: newExperience.title,
          description: newExperience.description.filter((desc) => desc.trim() !== ""),
        }),
      });

      if (response.ok) {
          const updated = await response.json();
          if (editingExperience) {
            setExperienceData((prev) =>
              prev.map((item) => (item.id === updated.id ? updated : item))
            );
            Swal.fire("Oldu Gardaş", "Helal");
          } else {
            setExperienceData((prev) => [...prev, updated]);
            Swal.fire("Oldu Oldu", "Helaaall!");
          }
          setShowForm(false);
          setEditingExperience(null);
          setNewExperience({ title: "", description: [""] });
        } else {
          Swal.fire("Bi sıkıntı var", "kaydedemedik", "error");
        }

    } catch (error) {
      console.error("backendde hata var", error);
    }
  };

  // Silme fonksiyonu
  const handleDeleteExperience = async () => {
    const token = sessionStorage.getItem("token");
    try {
      const response = await fetch(
        `http://localhost:3003/experience/deleteText/${experienceToDelete.id}`,
        {
          method: "DELETE",
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      if (response.ok) {
            setExperienceData((prev) =>
              prev.filter((item) => item.id !== experienceToDelete.id)
            );
            setDeleteConfirmOpen(false);
            setExperienceToDelete(null);
            Swal.fire("Silindi!", "tamam tamam", "sildin");
          } else {
            Swal.fire("Yok!", "başaramadın", "şaka şaka ben başaramamışımdır");
          }
    } catch (error) {
      console.error("bi sıkıntı var arkalarda", error);
    }
  };

  // Düzenle butonuna basıldığında formu aç, mevcut verileri set et
  const startEditing = (item) => {
    setEditingExperience(item);
    setNewExperience({
      title: item.title,
      description: item.descriptions,
    });
    setShowForm(true);
  };

  return (
    <Box id="experience" sx={{ py: 6, px: 2 }}>
      <Typography
        variant="h4"
        align="center"
        gutterBottom
        sx={{
          mb: 4,
          fontWeight: "bold",
          bgcolor: "white",
          WebkitBackgroundClip: "text",
          WebkitTextFillColor: "transparent",
          backgroundClip: "text",
          textFillColor: "transparent",
        }}
      >
        Deneyim
      </Typography>

      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
        }}
      >
        <Timeline
          position="right"
          sx={{
            maxWidth: "800px",
            width: "100%",
            "& .MuiTimelineItem-root:before": {
              flex: 0,
              padding: 0,
            },
          }}
        >
          {experienceData.map((item, index) => (
            <TimelineItem key={item.id}>
              <TimelineSeparator
                sx={{
                  position: "relative",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                {index !== experienceData.length - 1 && (
                  <TimelineConnector
                    sx={{
                      position: "absolute",
                      top: 0,
                      bottom: 0,
                      left: "50%",
                      transform: "translateX(-50%)",
                      zIndex: 1,
                      bgcolor: "primary.main",
                      width: 2,
                    }}
                  />
                )}
                <TimelineDot
                  variant="outlined"
                  sx={{
                    bgcolor: "white",
                    border: "none",
                    zIndex: 2,
                  }}
                >
                  <WorkspacesIcon color="primary" />
                </TimelineDot>
              </TimelineSeparator>

              <TimelineContent
                sx={{
                  display: "flex",
                  justifyContent: "center",
                  mb: 2,
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                <Paper
                  elevation={3}
                  sx={{
                    p: 2,
                    color: "black",
                    background: "linear-gradient(100deg, #03346E,rgb(69, 131, 207))",
                    borderRadius: 2,
                    maxWidth: "600px",
                    width: "100%",
                    "&:hover": {
                      transform: "scale(1.1)",
                      filter:
                        "drop-shadow(0 0 1px #E2E2B6) drop-shadow(0 0 5px #E2E2B6)",
                    },
                    position: "relative",
                  }}
                >
                  <Typography
                    variant="h6"
                    sx={{
                      fontWeight: "bold",
                      textAlign: "center",
                      color: "white",
                      pb: 4,
                    }}
                  >
                    {item.title}
                  </Typography>
                  {item.descriptions.map((desc, i) => (
                    <Typography
                      key={i}
                      variant="body2"
                      sx={{
                        textAlign: "center",
                        color: "white",
                        p: 1,
                      }}
                    >
                      • {desc}
                    </Typography>
                  ))}

                  {isAdmin && (
                    <Box
                      sx={{
                        display: "flex",
                        justifyContent: "center",
                        gap: 2,
                        mt: 2,
                      }}
                    >
                      <Button
  variant="outlined"
  onClick={() => startEditing(item)}
  sx={{
    backgroundColor: "white",
    color: "black",
    borderColor: "black",
    '&:hover': {
      backgroundColor: "#f0f0f0",
      borderColor: "black",
    },
  }}
>
  Düzenle
</Button>

<Button
  variant="outlined"
  onClick={() => {
    Swal.fire({
      title: "Emin misiniz?",
      text: "Bu deneyimi silmek istiyor musunuz?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#d33",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Evet, sil!",
      cancelButtonText: "Vazgeç",
    }).then((result) => {
      if (result.isConfirmed) {
        setExperienceToDelete(item);
        handleDeleteExperience();
      }
    });
  }}
  sx={{
    backgroundColor: "white",
    color: "black",
    borderColor: "black",
    '&:hover': {
      backgroundColor: "#f0f0f0",
      borderColor: "black",
    },
  }}
>
  Sil
</Button>


                    </Box>
                  )}
                </Paper>
              </TimelineContent>
            </TimelineItem>
          ))}
        </Timeline>
      </Box>

      {/* Admin ise ekleme butonu */}
      {isAdmin && !showForm && (
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            mb: 2,
            
          }}
        >
          <Button
            variant="outlined"
            onClick={() => {
              setShowForm(true);
              setEditingExperience(null);
              setNewExperience({ title: "", description: [""] });
            }}
            sx={{
              backgroundColor: "white",
              color: "black",
              borderColor: "black",
              '&:hover': {
                backgroundColor: "#f0f0f0",
                borderColor: "black",
              },
            }}
          >
            Ekleme Yap
          </Button>
        </Box>
      )}

      {/* Ekleme / Düzenleme Formu */}
      {showForm && (
        <Box
          sx={{
            mt: 4,
            display: "flex",
            flexDirection: "column",
        gap: 2,
        maxWidth: "600px",
        margin: "auto",
      }}
    >
      <TextField
        label="Başlık"
        value={newExperience.title}
        onChange={(e) =>
          setNewExperience({ ...newExperience, title: e.target.value })
        }
      />
      {newExperience.description.map((desc, index) => (
        <TextField
          key={index}
          label={`Açıklama ${index + 1}`}
          value={desc}
          onChange={(e) => {
            const updatedDescription = [...newExperience.description];
            updatedDescription[index] = e.target.value;
            setNewExperience({
              ...newExperience,
              description: updatedDescription,
            });
          }}
        />
      ))}
      <Button
        variant="outlined"
        onClick={() =>
          setNewExperience({
            ...newExperience,
            description: [...newExperience.description, ""],
          })
        }
      >
        + Yeni Açıklama
      </Button>
      <Button variant="contained" onClick={handleSaveExperience}>
        {editingExperience ? "Güncelle" : "Kaydet"}
      </Button>
    </Box>
  )}

  {/* Silme Onay Penceresi */}
  <Dialog
    open={deleteConfirmOpen}
    onClose={() => setDeleteConfirmOpen(false)}
  >
    <DialogTitle>Silme Onayı</DialogTitle>
    <DialogContent>
      <DialogContentText>
        Bu deneyimi silmek istediğinizden emin misiniz?
      </DialogContentText>
    </DialogContent>
    <DialogActions>
      <Button onClick={() => setDeleteConfirmOpen(false)}>Vazgeç</Button>
      <Button onClick={handleDeleteExperience} color="error">
        Sil
      </Button>
    </DialogActions>
  </Dialog>
</Box>
);
};

export default Experience;
