import { Box, Typography } from '@mui/material';

const About = () => {
  return (
    <Box
      sx={{
        minHeight: '100vh',

        color: '#f5f5dc',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        px: { xs: 2, sm: 4, md: 6, lg: 8, xl: 12 },
        py: { xs: 4, sm: 6, md: 8, lg: 10, xl: 12 },
        pt: { xs: 10, sm: 10, md:10, lg: 10, xl: 16},
      }}
    >
      
      <Box
        component="img"
        src="img1.webp"
        alt="Profil Resmi"
        sx={{
          
          width: { xs: 220, sm: 180, md: 160, lg: 140, xl: 200 },
          height: { xs: 220, sm: 180, md: 160, lg: 140, xl: 200 },
          borderRadius: '80%',
          boxShadow: `
            0 0 10px 2px rgb(250, 249, 252),
            0 0 20px 6px rgb(255, 255, 255),
            0 0 30px 10px rgb(23, 6, 83)
          `,
          mb: { xs: 2, sm: 3, md: 4, lg: 5, xl: 6 },
          objectFit: 'cover',
        }}
      />

      {/* İsim */}
      <Typography
        variant="h4"
        sx={{
          fontWeight: 'bold',
          color: '#FFF', 
          mb: { xs: 1, sm: 1.5, md: 2, lg: 2.5, xl: 3 },
          textAlign: 'center',
          textShadow: '0 0 40px rgb(0, 0, 0)',
        }}
      >
        Yakup Şimşek
      </Typography>

      {/* Kutular kapsayıcı */}
      <Box>
       
        <Box
        sx={{
          bgcolor: '#000B58', 
          color: '#A9B5DF',   
          px: { xs: 2, sm: 3, md: 4 },
          py: { xs: 2, sm: 3, md: 4 },
          borderRadius: 10,
          fontWeight: 'bold',
          fontSize: { xs: '1rem', sm: '1.1rem', md: '1.2rem' },
          textAlign: 'center',
          lineHeight: 1.5,
          width: { xs: '4in', sm: '4in', md: '4in', lg: '4in', xl: '5in' },
        }}
      >
Eğitim Teknoloğu
          <br />
          <Typography
            component="span"
            sx={{
              display: 'block',
              fontWeight: '500',
              fontSize: { xs: '0.9rem', sm: '1rem', md: '1.1rem' },
              mt: 1,
              color: '#A9B5DF',
              lineHeight: 1.4,
              mt:2
            }}
          >
İnteraktif içerik geliştirme konusunda tutkulu
          </Typography>
        </Box>

      </Box>
    </Box>
  );
};

export default About;
