const experienceData =[
{
title : 'PixBuff',
description: 
[
    "Eğitim Teknoloğu pozisyonunda Articulate Storyline 360 programını kullanarak etkileşimli içerikler geliştirdim.",
    "Ayrıca, animasyon videoları için Adobe Illustrator kullanarak grafik tasarımlarının hazırlanmasına destek verdim."
]},


{
    title: 'Hacettepe Üniversitesi - HÜZEM (Uzaktan Eğitim Uygulama ve Araştırma Merkezi)',
    description: [
        "Kısmi zamanlı öğrenci olarak Articulate Storyline 360 ile etkileşimli e-öğrenme içerikleri tasarladım." +
        "Hacettepe Üniversitesi’nin uzaktan eğitim sistemi olan Moodle LMS üzerinde düzenlemeler yaptım ve sistemin geliştirilmesine katkı sağladım.",
        "Çoğunlukla bu tür teknik ve içerik geliştirme görevlerinde yer aldım."
    ]
},

{
    title: 'Teknofen Koleji',
    description:[
        "Yarım dönem boyunca stajyer öğretmen olarak görev aldım.",
        "Lise düzeyinde 9., 10. ve 11. sınıfların bilgisayar derslerine girdim.",
        "Bu derslerde öğrencilere C, C++ ve Adobe After Effects yazılımlarını öğrettim."
    ]
}
]


export default experienceData ;
