import React from "react";
import {
  Box,
  Typography,
  Paper,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
} from "@mui/lab";
import WorkIcon from "@mui/icons-material/Work";
import experienceData from "../data/experience";

const Experience = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <Box
      id="experience"
      sx={{
        py: 6,
        px: 2,
        display: "flex",
        justifyContent: "center",
        backgroundColor: "#f9f9f9",
      }}
    >
      <Timeline position="right" sx={{ maxWidth: "800px", width: "100%" }}>
        {experienceData.map((item, index) => (
          <TimelineItem key={index}>
            <TimelineSeparator>
              <TimelineDot color="primary">
                <WorkIcon />
              </TimelineDot>
              {index !== experienceData.length - 1 && <TimelineConnector />}
            </TimelineSeparator>

            <TimelineContent
              sx={{
                display: "flex",
                justifyContent: "center", // kutuyu ortalamak için
                mb: 2,
              }}
            >
              <Paper
                elevation={3}
                sx={{
                  p: 2,
                  bgcolor: "#f0f4ff",
                  borderRadius: 2,
                  maxWidth: "600px",
                  width: "100%",
                }}
              >
                <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                  {item.title}
                </Typography>
                {item.description.map((desc, i) => (
                  <Typography key={i} variant="body2">
                    • {desc}
                  </Typography>
                ))}
              </Paper>
            </TimelineContent>
          </TimelineItem>
        ))}
      </Timeline>
    </Box>
  );
};

export default Experience;
