import React from "react";
import {
  Box,
  Typography,
  Paper,
  useMediaQuery,
  useTheme,
} from "@mui/material";
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
} from "@mui/lab";
import WorkIcon from "@mui/icons-material/Work";
import experienceData from "../data/experience";

const Experience = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <Box
      id="experience"
      sx={{
        py: 6,
        px: 2,
      }}
    >
      
      <Typography variant="h4" align="center" gutterBottom sx={{ mb: 4 }}>
        Deneyim
      </Typography>

     {/* timeline */}
      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
        }}
      >
        <Timeline
          position="right"
          sx={{
            maxWidth: "800px",
            width: "100%",
            "& .MuiTimelineItem-root:before": {
              flex: 0,
              padding: 0,
            },
            
          }}
        >
          {experienceData.map((item, index) => (
            <TimelineItem key={index}>
              <TimelineSeparator>
                <TimelineDot color="primary">
                  <WorkIcon />
                </TimelineDot>
                {index !== experienceData.length - 1 && <TimelineConnector />}
              </TimelineSeparator>

              <TimelineContent
                sx={{
                  display: "flex",
                  justifyContent: "center",
                  mb: 2,
                }}
              >
                <Paper
                  elevation={3}
                  sx={{
                    p: 2,
                    color:'white',
                    background: 'linear-gradient(to bottom, #CDC1FF, #9e219e)',
                    borderRadius: 2,
                    maxWidth: "600px",
                    width: "100%",
                    // filter: 'drop-shadow(0 0 6px rgb(18, 38, 214)) drop-shadow(0 0 10px rgb(61, 14, 148))',
                      '&:hover': {
                        transform: 'scale(1.1)',
                        filter: 'drop-shadow(0 0 6px rgb(121, 129, 202)) drop-shadow(0 0 10px rgb(196, 180, 226))',
                      },
                  }}
                >
                  <Typography variant="h6"
                   sx={{
                    fontWeight: "bold",
                    textAlign:'center',
                    pb:4
                       }}>
                    {item.title}
                  </Typography>
                  {item.description.map((desc, i) => (
                    <Typography key={i}
                     variant="body2"
                     sx={{
                      textAlign:'center',
                      p:1
                     }}
                     >
                      • {desc}
                    </Typography>
                  ))}
                </Paper>
              </TimelineContent>
            </TimelineItem>
          ))}
        </Timeline>
      </Box>
    </Box>
  );
};

export default Experience;
