import { Box, Typography } from '@mui/material';

const About = () => {
  return (
    <Box
      sx={{
        px: 4,
        py: 2,
        ml:10,
        mr:10,
        pt:5,
      }}
    >
     
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          mb: 1,
        }}
      >
        <Typography variant="h3" fontWeight="bold"
           sx={{
            color:'red'
          }}>
            Yakup <br />
            <Box component="span" sx={{ display: 'block', mt: 1 }}>
              ŞİMŞEK
            </Box>
          </Typography>



        <Box sx={{ display: 'flex', gap: 2 }}>
          <Box
            component="img"
            src="img1.webp"
            alt="Resim 1"
            sx={{ width: 300, height: 450, objectFit: 'cover' }}
          />
          <Box
            component="img"
            src="img1.webp"
            alt="Resim 2"
            sx={{ width: 300, height: 450, objectFit: 'cover' }}
          />
        </Box>
      </Box>

      {/* Alt satır */}
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          pt: 1,
        }}
      >
        <Typography 
        variant="h5"
        sx={{
          color:'white'
        }}>
          Eğitim Teknoloğu
          </Typography>


        <Typography 
        variant="body1"
        sx={{
          maxWidth: '80%',
          textAlign: 'right',
          color:'white',

          
          }}>
            Teknolojinin eğitimi dönüştürme gücüne inanarak, öğrencilere ve eğitimcilere yenilikçi çözümler sunmayı
            hedefliyorum. Üniversite eğitimim boyunca çeşitli projelerde yer alarak eğitimde dijital dönüşüm üzerine
            çalışmalar yaptım. Özellikle öğrenme yönetim sistemlerinin (LMS) kullanımı, interaktif içerik geliştirme
            ve veri analitiği ile öğrenme süreçlerini iyileştirme konularında kendimi geliştirdim. Ekip çalışmasına büyük
            değer verir, eleştirilere açık bir şekilde sürekli öğrenme arzusu içindeyim. Yaratıcılığım, metinleri hayata
            geçirme, hikayeler anlatma ve izleyici üzerinde etkili olma yeteneğimle öne çıkarım. Her proje bana yeni
            bir hikaye anlatma fırsatı sunar ve bu da beni sürekli motive eder.

        </Typography>
      </Box>
    </Box>
  );
};

export default About;
