// components/Navbar.jsx
import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, IconButton, Typography, Box, Drawer, List, ListItem, ListItemText } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

const sections = [
  { label: 'Hakkımda', id: 'hakkimda' },
  { label: 'Eğitim', id: 'egitim' },
  { label: 'Deneyim', id: 'deneyim' },
  { label: 'Projeler', id: 'projeler' },
  { label: 'Yetenekler', id: 'yetenekler' },
  { label: 'İletişim', id: 'iletisim' },
];

const Navbar = () => {
  const [elevated, setElevated] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);

  const handleScroll = () => {
    setElevated(window.scrollY > 10);
  };

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setDrawerOpen(false); // Mobilde menüyü kapat
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <>
      <AppBar
        position="fixed"
        elevation={0}
        sx={{
          backgroundColor: elevated ? 'rgba(255, 255, 255, 0.7)' : '#CDC1FF',
          backdropFilter: elevated ? 'blur(10px)' : 'none',
          transition: 'all 0.3s ease',
          color: 'black',
        }}
      >
        <Toolbar sx={{
          display: 'flex',
          justifyContent: 'space-between',
          px: { xs: 2, md: 40 },
          display:'flex'

             }}>
          <Typography variant="h6">Benim Portfolyom</Typography>

          {/* Masaüstü görünüm */}
          <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 3 }}>
            {sections.map((section) => (
              <Typography
                key={section.id}
                onClick={() => scrollToSection(section.id)}
                sx={{ cursor: 'pointer' }}
              >
                {section.label}
              </Typography>
            ))}
          </Box>

          {/* Mobil görünüm */}
          <Box sx={{ display: { xs: 'block', md: 'none' } }}>
            <IconButton onClick={() => setDrawerOpen(true)}>
              <MenuIcon sx={{ color: 'black' }} />
            </IconButton>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Mobil menü drawer */}
      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        PaperProps={{
          sx: {
            backgroundColor: '#CDC1FF',
            color: '#CDC1FF',
          },
        }}
      >
        <List>
          {sections.map((section) => (
            <ListItem button key={section.id} onClick={() => scrollToSection(section.id)}>
              <ListItemText primary={section.label} />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </>
  );
};

export default Navbar;
