import React from 'react';
import { Box, Typography, Avatar, IconButton } from '@mui/material';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';
import InstagramIcon from '@mui/icons-material/Instagram';
import aboutData from '../data/about';

const About = () => {
  return (
    <Box
  sx={{
    minHeight: '100vh',
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    bgcolor: '#e6f2f0',
    p: 2,
    overflowY: 'auto',
  }}
>
  <Box
    sx={{
      display: 'flex',
      flexDirection: { xs: 'column', md: 'row' },
      width: { xs: '100%', sm: '95%', md: '85%' },
      maxWidth: '1200px', // yeni eklendi
      boxShadow: 6,
      borderRadius: 4,
      overflow: 'hidden',
    }}
  >
    {/* Sol Kısım */}
    <Box
      sx={{
        width: { xs: '100%', md: '30%' },
        background: 'linear-gradient(to bottom, #7ed957, #28a745, #cceeff)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        p: 3,
      }}
    >
      ...
    </Box>

    {/* Sağ Kısım */}
    <Box
      sx={{
        width: { xs: '100%', md: '75%' }, // genişlik artırıldı
        bgcolor: 'white',
        p: 4,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        borderTopLeftRadius: { xs: 0, md: 40 },
        borderBottomLeftRadius: { xs: 0, md: 40 },
        ml: { xs: 0, md: '-30px' },
        zIndex: 1,
        overflow: 'hidden', // yeni
        wordBreak: 'break-word', // yeni
      }}
    >
          {aboutData.map((item, index) => (
            <Typography key={index} variant="body1" sx={{ color: '#333', mb: 2 }}>
              • {item}
            </Typography>
          ))}

          <Box sx={{ display: 'flex', alignItems: 'center', mt: 2, flexWrap: 'wrap' }}>
            <MailOutlineIcon sx={{ color: '#001f4d', mr: 1 }} />
            <Box
              onClick={() => window.open('mailto:yakupsimseke@gmail.com')}
              sx={{
                display: 'flex',
                alignItems: 'center',
                gap: 1,
                px: 2,
                py: 1,
                bgcolor: 'white',
                borderRadius: 2,
                cursor: 'pointer',
                border: '2px solid #001f4d',
                color: 'black',
                transition: '0.3s',
                '&:hover': {
                  bgcolor: '#001f4d',
                  color: 'white',
                },
                mt: { xs: 1, md: 0 },
              }}
            >
              <Typography variant="body1" sx={{ fontWeight: 600 }}>
                Benimle İletişime Geçin
              </Typography>
              <ArrowForwardIosIcon sx={{ fontSize: 16 }} />
            </Box>
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default About;
