import React from 'react';
import { Box, Typography, Avatar, IconButton, Grid } from '@mui/material';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';
import InstagramIcon from '@mui/icons-material/Instagram';

const neonIconStyle = {
  color: 'white',
  transition: 'transform 0.3s ease, filter 0.3s ease',
  filter: 'drop-shadow(0 0 6px rgb(0, 0, 0)) drop-shadow(0 0 10px rgb(61, 14, 148))',
  '&:hover': {
    transform: 'scale(1.3)',
    filter: 'drop-shadow(0 0 12px rgb(255, 255, 255)) drop-shadow(0 0 20px #ffbbff)',
  },
};

const About = () => {
  return (
    <Box
      sx={{
        minHeight: '100vh',
        p: 2,
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        bgcolor: '#f5f5f5',
      }}
    >
      <Box
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', md: 'row' },
          width: '100%',
          maxWidth: 1200,
          boxShadow: 6,
          borderRadius: 4,
          overflow: 'hidden',
        }}
      >
        {/* Sol Kısım */}
        <Box
          sx={{
            flex: { xs: 'none', md: '0 0 35%' },
            background: 'linear-gradient(to bottom, rgb(27,186,214), rgb(12,150,192), rgb(60,48,218))',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            p: 4,
          }}
        >
          <Avatar
            src="asd.jpg"
            alt="Yakup Şimşek"
            sx={{
              width: { xs: 180, sm: 200, md: 240 },
              height: { xs: 180, sm: 200, md: 240 },
              mb: 2,
              border: '3px solid white',
            }}
          />
          <Typography variant="h4" sx={{ color: 'white', mb: 1, textAlign: 'center' }}>
            Yakup Şimşek
          </Typography>
          <Typography variant="h6" sx={{ color: 'white', mb: 2, textAlign: 'center' }}>
            Eğitim Teknoloğu
          </Typography>
          <Box>
            {[LinkedInIcon, GitHubIcon, InstagramIcon].map((Icon, idx) => (
              <IconButton
                key={idx}
                sx={{
                  transition: 'all 0.3s ease',
                  '&:hover svg': neonIconStyle['&:hover'],
                }}
              >
                <Icon sx={{ fontSize: 30, ...neonIconStyle }} />
              </IconButton>
            ))}
          </Box>
        </Box>

        {/* Sağ Kısım */}
        <Box
          sx={{
            flex: 1,
            bgcolor: 'white',
            p: { xs: 3, sm: 4 },
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
            alignItems: 'center',
          }}
        >
          <Typography
            variant="h4"
            sx={{
              color: '#001f4d',
              fontWeight: 'bold',
              mb: 4,
              textAlign: 'center',
            }}
          >
            Hakkımda
          </Typography>

          <Grid container spacing={3} justifyContent="center">
            {[
              'Yenilikçi Çözümler',
              'Dijital Dönüşüm',
              'İnteraktif İçerikler',
              'Ekip Çalışmaları',
              'Etkili Yaratıcılık',
              'Devamlı Motivasyon',
            ].map((text, index) => (
              <Grid
                item
                xs={12}
                sm={6}
                md={4}
                key={index}
                sx={{
                  display: 'flex',
                  justifyContent: 'center',
                }}
              >
                <Box
                  sx={{
                    bgcolor: '#FFF',
                    p: 3,
                    borderRadius: 4,
                    boxShadow: 3,
                    textAlign: 'center',
                    width: { xs: '90%', sm: 200, md: 180 },
                    minHeight: 80,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    cursor: 'pointer',
                    transition: 'all 0.3s ease',
                    '&:hover': {
                      transform: 'translateY(-5px)',
                      boxShadow: 6,
                      bgcolor: '#2D336B',
                      color: 'white',
                    },
                  }}
                >
                  <Typography
                    variant="subtitle1"
                    sx={{
                      fontSize: { xs: '0.95rem', sm: '1rem', md: '1.1rem' },
                    }}
                  >
                    {text}
                  </Typography>
                </Box>
              </Grid>
            ))}
          </Grid>

          <Box
            sx={{
              display: 'flex',
              alignItems: 'center',
              mt: 4,
              flexWrap: 'wrap',
              justifyContent: 'center',
            }}
          >
            <MailOutlineIcon sx={{ mr: 1 }} />
            <Box
              onClick={() => window.open('mailto:yakupsimseke@gmail.com')}
              sx={{
                display: 'flex',
                alignItems: 'center',
                gap: 1,
                px: 2,
                py: 1,
                bgcolor: 'white',
                borderRadius: 20,
                cursor: 'pointer',
                border: '2px solid rgb(11, 151, 194)',
                color: 'black',
                transition: '0.3s',
                '&:hover': {
                  bgcolor: 'rgb(11, 151, 194)',
                  color: 'white',
                  border: '2px solid white',
                },
              }}
            >
              <Typography variant="body1">Benimle İletişime Geçin</Typography>
              <ArrowForwardIosIcon
                sx={{
                  color: '#0b97c2',
                  fontSize: 16,
                  transition: '0.3s',
                }}
              />
            </Box>
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default About;
