import React from 'react';
import { Box, Typography, Grid, Paper } from '@mui/material';
import BuildIcon from '@mui/icons-material/Build';
import DesignServicesIcon from '@mui/icons-material/DesignServices';
import CodeIcon from '@mui/icons-material/Code';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';

const services = [
  {
    icon: <BuildIcon sx={{ fontSize: 50, color: '#90caf9' }} />,
    title: 'Web Geliştirme',
    description: 'Modern ve duyarlı web uygulamaları geliştiriyorum.',
  },
  {
    icon: <DesignServicesIcon sx={{ fontSize: 50, color: '#90caf9' }} />,
    title: 'UI/UX Tasarım',
    description: 'Kullanıcı dostu ve şık tasarımlar oluşturuyorum.',
  },
  {
    icon: <CodeIcon sx={{ fontSize: 50, color: '#90caf9' }} />,
    title: 'Yazılım Danışmanlığı',
    description: 'Proje ihtiyaçlarınıza özel çözümler sunuyorum.',
  },
  {
    icon: <SupportAgentIcon sx={{ fontSize: 50, color: '#90caf9' }} />,
    title: 'Teknik Destek',
    description: 'Sorunlarınızı hızlı ve etkili şekilde çözüyorum.',
  },
];

const Services = () => {
  return (
    <Box sx={{ maxWidth: 1200, mx: 'auto', mt: 6, px: 0 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom align="center" sx={{ px: 2 }}>
        Hizmetler
      </Typography>
      <Typography
        variant="body1"
        align="center"
        color="text.secondary"
        mb={6}
        maxWidth={700}
        mx="auto"
        sx={{ px: 2 }}
      >
        Profesyonel olarak verdiğim hizmetlere aşağıdan ulaşabilirsiniz
      </Typography>
      <Grid
        container
        spacing={3}
        sx={{ px: '20px' }} // sağ ve solda 20px boşluk
      >
        {services.map(({ icon, title, description }, index) => (
          <Grid
            item
            xs={12}
            sm={6}
            md={6}
            lg={3}
            key={index}
            sx={{ display: 'flex' }}
          >
            <Paper
              elevation={0}
              sx={{
                border: '2px solid #90caf9', // açık mavi kenarlık
                bgcolor: 'transparent', // arka plan yok
                p: 3,
                textAlign: 'left',
                borderRadius: 3,
                width: '100%',
                display: 'flex',
                alignItems: 'center',
                gap: 2,
                minHeight: 120,
                transition: 'transform 0.3s ease',
                '&:hover': {
                  transform: 'scale(1.05)',
                  borderColor: '#64b5f6',
                  boxShadow: '0 0 10px #90caf9',
                },
              }}
            >
              <Box sx={{ color: '#90caf9', fontSize: 50 }}>{icon}</Box>
              <Box>
                <Typography variant="h6" fontWeight="bold" gutterBottom>
                  {title}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {description}
                </Typography>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Services;
