import { Box, Typography } from '@mui/material';

const About = () => {
  return (
    <Box
      sx={{
        minHeight: '100vh',

        color: '#f5f5dc',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        px: { xs: 2, sm: 4, md: 6, lg: 8, xl: 12 },
        py: { xs: 4, sm: 6, md: 8, lg: 10, xl: 12 },
      }}
    >
      
      <Box
        component="img"
        src="img1.webp"
        alt="Profil Resmi"
        sx={{
          width: { xs: 120, sm: 150, md: 180, lg: 220, xl: 260 },
          height: { xs: 120, sm: 150, md: 180, lg: 220, xl: 260 },
          borderRadius: '50%',
          boxShadow: `
            0 0 10px 2px rgb(180, 19, 19),
            0 0 20px 6px rgb(6, 27, 59),
            0 0 30px 10px rgb(8, 28, 63)
          `,
          mb: { xs: 2, sm: 3, md: 4, lg: 5, xl: 6 },
          objectFit: 'cover',
        }}
      />

      {/* İsim */}
      <Typography
        variant="h4"
        sx={{
          fontWeight: '700',
          color: '#a3c9f1', // açık mavi ton
          mb: { xs: 1, sm: 1.5, md: 2, lg: 2.5, xl: 3 },
          textAlign: 'center',
          textShadow: '0 0 6px #5fa0ff',
        }}
      >
        Yakup Şimşek
      </Typography>

      {/* Kutular kapsayıcı */}
      <Box
        sx={{
          width: { xs: '100%', sm: '90%', md: '70%', lg: '50%', xl: '40%' },
          display: 'flex',
          flexDirection: 'column',
          gap: { xs: 1.5, sm: 2, md: 3 },
        }}
      >
        {/* Eğitim Teknoloğu kutusu */}
        <Box
          sx={{
            bgcolor: '#f5f5dc', // bej arka plan
            color: '#001f4d', // lacivert yazı
            px: { xs: 2, sm: 3, md: 4 },
            py: { xs: 1, sm: 1.5, md: 2 },
            borderRadius: 2,
            fontWeight: '600',
            fontSize: { xs: '1rem', sm: '1.1rem', md: '1.2rem' },
            boxShadow: '0 0 8px rgba(245,245,220,0.5)',
            textAlign: 'center',
          }}
        >
          Eğitim Teknoloğu
        </Box>

        {/* Interaktif içerik kutusu */}
        <Box
          sx={{
            bgcolor: '#a3c9f1', // açık mavi arka plan
            color: '#001f4d', // lacivert yazı
            px: { xs: 2, sm: 3, md: 4 },
            py: { xs: 1, sm: 1.5, md: 2 },
            borderRadius: 2,
            fontWeight: '500',
            fontSize: { xs: '0.9rem', sm: '1rem', md: '1.1rem' },
            boxShadow: '0 0 10px rgba(163,201,241,0.7)',
            textAlign: 'center',
            lineHeight: 1.4,
          }}
        >
          Interaktif içerik geliştirme konusunda tutkulu
        </Box>
      </Box>
    </Box>
  );
};

export default About;
