import React, { useEffect, useState } from "react";
import axios from "axios";
import jwtDecode from "jwt-decode";
import {
  Box,
  Typography,
  Paper,
  Button,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
} from "@mui/material";
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
} from "@mui/lab";
import WorkspacesIcon from "@mui/icons-material/Workspaces";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";

const API_BASE = "http://localhost:5000/experience"; // Backend URL'nizi ayarlayın

const Experience = () => {
  const [experienceData, setExperienceData] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);

  // Form kontrolü
  const [openDialog, setOpenDialog] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editId, setEditId] = useState(null);

  // Form verileri
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState([""]);

  // Token'dan isAdmin kontrolü
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setIsAdmin(decoded.isAdmin || false);
      } catch {
        setIsAdmin(false);
      }
    }
  }, []);

  // Backend'den deneyimleri çek
  const fetchExperiences = async () => {
    try {
      const res = await axios.get(`${API_BASE}/getText`);
      setExperienceData(res.data);
    } catch (e) {
      console.error("Fetch error", e);
    }
  };

  useEffect(() => {
    fetchExperiences();
  }, []);

  // Aç/Kapa dialog
  const handleOpenDialog = (edit = false, data = null) => {
    setEditMode(edit);
    if (edit && data) {
      setEditId(data.id);
      setTitle(data.title);
      setDescription(data.description.length > 0 ? data.description : [""]);
    } else {
      setEditId(null);
      setTitle("");
      setDescription([""]);
    }
    setOpenDialog(true);
  };

  const handleCloseDialog = () => setOpenDialog(false);

  // Description dizisine satır ekle
  const addDescriptionLine = () => {
    setDescription([...description, ""]);
  };

  // Description dizisinde değişiklik
  const handleDescriptionChange = (index, value) => {
    const newDesc = [...description];
    newDesc[index] = value;
    setDescription(newDesc);
  };

  // Delete deneyim
  const handleDelete = async (id) => {
    if (!window.confirm("Deneyimi silmek istediğinizden emin misiniz?")) return;

    try {
      const token = localStorage.getItem("token");
      await axios.delete(`${API_BASE}/deleteText/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchExperiences();
    } catch (e) {
      console.error("Delete error", e);
    }
  };

  // Add veya Update deneyim
  const handleSubmit = async () => {
    if (!title.trim() || description.some((desc) => !desc.trim())) {
      alert("Başlık ve açıklamalar boş olamaz.");
      return;
    }
    try {
      const token = localStorage.getItem("token");
      if (editMode) {
        // Update
        await axios.put(
          `${API_BASE}/updateText/${editId}`,
          { title, description },
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
      } else {
        // Add
        await axios.post(
          `${API_BASE}/addText`,
          { title, description },
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
      }
      fetchExperiences();
      handleCloseDialog();
    } catch (e) {
      console.error("Submit error", e);
      alert("Bir hata oluştu.");
    }
  };

  return (
    <Box
      id="experience"
      sx={{
        py: 6,
        px: 2,
      }}
    >
      <Typography
        variant="h4"
        align="center"
        gutterBottom
        sx={{
          mb: 4,
          fontWeight: "bold",
          bgcolor: "white",
          WebkitBackgroundClip: "text",
          WebkitTextFillColor: "transparent",
          backgroundClip: "text",
          textFillColor: "transparent",
        }}
      >
        Deneyim
      </Typography>

      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
        }}
      >
        <Timeline
          position="right"
          sx={{
            maxWidth: "800px",
            width: "100%",
            "& .MuiTimelineItem-root:before": {
              flex: 0,
              padding: 0,
            },
          }}
        >
          {experienceData.map((item, index) => (
            <TimelineItem key={item.id || index}>
              <TimelineSeparator
                sx={{
                  position: "relative",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                {index !== experienceData.length - 1 && (
                  <TimelineConnector
                    sx={{
                      position: "absolute",
                      top: 0,
                      bottom: 0,
                      left: "50%",
                      transform: "translateX(-50%)",
                      zIndex: 1,
                      bgcolor: "primary.main",
                      width: 2,
                    }}
                  />
                )}
                <TimelineDot
                  variant="outlined"
                  sx={{
                    bgcolor: "white",
                    border: "none",
                    zIndex: 2,
                  }}
                >
                  <WorkspacesIcon color="primary" />
                </TimelineDot>
              </TimelineSeparator>

              <TimelineContent
                sx={{
                  display: "flex",
                  justifyContent: "center",
                  mb: 2,
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                <Paper
                  elevation={3}
                  sx={{
                    p: 2,
                    color: "black",
                    background:
                      "linear-gradient(100deg, #03346E,rgb(69, 131, 207))",
                    borderRadius: 2,
                    maxWidth: "600px",
                    width: "100%",
                    position: "relative",
                    "&:hover": {
                      transform: "scale(1.1)",
                      filter:
                        "drop-shadow(0 0 1px #E2E2B6) drop-shadow(0 0 5px #E2E2B6)",
                    },
                  }}
                >
                  <Typography
                    variant="h6"
                    sx={{
                      fontWeight: "bold",
                      textAlign: "center",
                      color: "white",
                      pb: 4,
                    }}
                  >
                    {item.title}
                  </Typography>
                  {item.description.map((desc, i) => (
                    <Typography
                      key={i}
                      variant="body2"
                      sx={{
                        textAlign: "center",
                        color: "white",
                        p: 1,
                      }}
                    >
                      • {desc}
                    </Typography>
                  ))}

                  {/* Admin işlemleri */}
                  {isAdmin && (
                    <Box
                      sx={{
                        position: "absolute",
                        top: 8,
                        right: 8,
                        display: "flex",
                        gap: 1,
                      }}
                    >
                      <IconButton
                        aria-label="edit"
                        size="small"
                        sx={{ color: "white" }}
                        onClick={() => handleOpenDialog(true, item)}
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton
                        aria-label="delete"
                        size="small"
                        sx={{ color: "white" }}
                        onClick={() => handleDelete(item.id)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </Box>
                  )}
                </Paper>
              </TimelineContent>
            </TimelineItem>
          ))}
        </Timeline>
      </Box>

      {/* Admin: Add Button */}
      {isAdmin && (
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            mt: 4,
          }}
        >
          <Button variant="contained" onClick={() => handleOpenDialog(false)}>
            Yeni Deneyim Ekle
          </Button>
        </Box>
      )}

      {/* Dialog for Add/Edit */}
      <Dialog open={openDialog} onClose={handleCloseDialog} fullWidth maxWidth="sm">
        <DialogTitle>{editMode ? "Deneyim Düzenle" : "Yeni Deneyim Ekle"}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Başlık"
            type="text"
            fullWidth
            variant="outlined"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle1" gutterBottom>
              Açıklamalar
            </Typography>
            {description.map((desc, index) => (
              <TextField
                key={index}
                margin="dense"
                label={`Açıklama ${index + 1}`}
                type="text"
                fullWidth
                variant="outlined"
                value={desc}
                onChange={(e) => handleDescriptionChange(index, e.target.value)}
                sx={{ mb: 1 }}
              />
            ))}
            <Button variant="text" onClick={addDescriptionLine}>
              + Yeni Açıklama Satırı Ekle
            </Button>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>İptal</Button>
          <Button onClick={handleSubmit} variant="contained" color="primary">
            {editMode ? "Güncelle" : "Ekle"}
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Experience;
