import React from 'react';
import { Box, Typography, Grid, Paper } from '@mui/material';
import BuildIcon from '@mui/icons-material/Build';
import DesignServicesIcon from '@mui/icons-material/DesignServices';
import CodeIcon from '@mui/icons-material/Code';
import SupportAgentIcon from '@mui/icons-material/SupportAgent';

const services = [
  {
    icon: <BuildIcon sx={{ fontSize: 60, color: '#90caf9' }} />, // İkon büyüdü
    title: 'Web Geliştirme',
    description: 'Modern ve duyarlı web uygulamaları geliştiriyorum.',
  },
  {
    icon: <DesignServicesIcon sx={{ fontSize: 60, color: '#90caf9' }} />,
    title: 'UI/UX Tasarım',
    description: 'Kullanıcı dostu ve şık tasarımlar oluşturuyorum.',
  },
  {
    icon: <CodeIcon sx={{ fontSize: 60, color: '#90caf9' }} />,
    title: 'Yazılım Danışmanlığı',
    description: 'Proje ihtiyaçlarınıza özel çözümler sunuyorum.',
  },
  {
    icon: <SupportAgentIcon sx={{ fontSize: 60, color: '#90caf9' }} />,
    title: 'Teknik Destek',
    description: 'Sorunlarınızı hızlı ve etkili şekilde çözüyorum.',
  },
];

const Services = () => {
  return (
    <Box sx={{ maxWidth: 1200, mx: 'auto', mt: 6, px: { xs: 2, md: 4 } }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom align="center">
        Hizmetler
      </Typography>
      <Typography
        variant="body1"
        align="center"
        color="text.secondary"
        mb={6}
        maxWidth={700}
        mx="auto"
      >
        Profesyonel olarak verdiğim hizmetlere aşağıdan ulaşabilirsiniz
      </Typography>
      <Grid container spacing={4}>
        {services.map(({ icon, title, description }, index) => (
          <Grid
            item
            xs={12}
            sm={6}
            md={6}
            lg={3}
            key={index}
            sx={{ display: 'flex' }}
          >
            <Paper
              elevation={4}
              sx={{
                bgcolor: '#f5f5f5',
                p: 4,
                textAlign: 'center',
                borderRadius: 3,
                width: '100%',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                // Yüksekliği artırıyoruz:
                minHeight: { xs: 250, sm: 320, md: 360, lg: 380 },
                boxShadow:
                  '0px 4px 20px rgba(144, 202, 249, 0.3)', // hafif mavi gölge
                transition: 'transform 0.3s ease',
                '&:hover': {
                  transform: 'scale(1.05)',
                  boxShadow:
                    '0px 8px 30px rgba(144, 202, 249, 0.6)', // hover gölge
                },
              }}
            >
              <Box sx={{ mb: 3 }}>{icon}</Box>
              <Typography variant="h5" fontWeight="bold" gutterBottom>
                {title}
              </Typography>
              <Typography variant="body1" color="text.secondary" sx={{ flexGrow: 1 }}>
                {description}
              </Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Services;
