import React, { useState, useEffect } from 'react';
import {
  AppBar,
  Toolbar,
  IconButton,
  Typography,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  Backdrop,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';

const sections = [
  { label: 'Hakkımda', id: 'about' },
  { label: 'Eğitim', id: 'education' },
  { label: 'Deneyim', id: 'experience' },
  { label: 'Projeler', id: 'projects' },
  { label: 'Yetenekler', id: 'skills' },
  { label: 'İletişim', id: 'contact' },
];

export default function Navbar() {
  const [elevated, setElevated] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    const onScroll = () => setElevated(window.scrollY > 10);
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
      setDrawerOpen(false);
    }
  };

  return (
    <>
      <AppBar
        position="fixed"
        elevation={0}
        sx={{
          backgroundColor: elevated ? 'rgba(255,255,255,0.7)' : '#CDC1FF',
          backdropFilter: elevated ? 'blur(10px)' : 'none',
          color: 'black',
          transition: 'all 0.3s ease',
        }}
      >
        <Toolbar
          sx={{
            justifyContent: 'space-between',
            px: { xs: 2, md: 10 },
          }}
        >
          <Typography
            variant="h6"
            sx={{ cursor: 'pointer' }}
            onClick={() => scrollToSection('about')}
          >
            Benim Portfolyom
          </Typography>

          {/* Masaüstü Menü */}
          <Box
            sx={{
              display: { xs: 'none', md: 'flex' },
              gap: 4,
              alignItems: 'center',
            }}
          >
            {sections.map(({ label, id }) => (
              <Typography
                key={id}
                sx={{ cursor: 'pointer', userSelect: 'none' }}
                onClick={() => scrollToSection(id)}
              >
                {label}
              </Typography>
            ))}
          </Box>

          {/* Mobil hamburger */}
          <Box sx={{ display: { xs: 'block', md: 'none' } }}>
            <IconButton
              onClick={() => setDrawerOpen(true)}
              sx={{ color: 'black', backgroundColor: 'transparent' }}
              aria-label="menu"
            >
              <MenuIcon />
            </IconButton>
          </Box>
        </Toolbar>
      </AppBar>

      {/* Backdrop (sayfa karartma, menü açıkken) */}
      <Backdrop
        open={drawerOpen}
        onClick={() => setDrawerOpen(false)}
        sx={{ zIndex: (theme) => theme.zIndex.drawer - 1, backgroundColor: 'rgba(0,0,0,0.3)' }}
      />

      {/* Drawer */}
      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        PaperProps={{
          sx: {
            width: '70vw',
            maxWidth: 300,
            backgroundColor: '#CDC1FF',
            color: 'black',
            px: 3,
            py: 4,
          },
        }}
      >
        <List sx={{ height: '100%' }}>
          {sections.map(({ label, id }) => (
            <ListItem button key={id} onClick={() => scrollToSection(id)}>
              <ListItemText
                primary={label}
                primaryTypographyProps={{ fontWeight: 'bold' }}
              />
            </ListItem>
          ))}
        </List>
      </Drawer>
    </>
  );
}
