import React from 'react';
import { Box, Typography, Grid, Avatar } from '@mui/material';
import BrushIcon from '@mui/icons-material/Brush';         // Adobe Illustrator gibi
import VideoLibraryIcon from '@mui/icons-material/VideoLibrary'; // After Effects
import CropOriginalIcon from '@mui/icons-material/CropOriginal'; // Canva
import LanguageIcon from '@mui/icons-material/Language';   // WordPress (internet sitesi)
import SchoolIcon from '@mui/icons-material/School';       // LMS

const skills = [
  { name: 'Articulate Storyline 360', icon: <SchoolIcon /> },
  { name: 'Adobe Illustrator', icon: <BrushIcon /> },
  { name: 'Adobe After Effects', icon: <VideoLibraryIcon /> },
  { name: 'WordPress', icon: <LanguageIcon /> },
  { name: 'Canva', icon: <CropOriginalIcon /> },
  // LMS Sistemi için SchoolIcon tekrar kullanıldı
];

const Skills = () => {
  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', mt: 5 }}>
      <Typography variant="h4" align="center" gutterBottom fontWeight="bold">
        Yetenekler
      </Typography>
      <Grid container spacing={4} justifyContent="center">
        {skills.map(({ name, icon }, index) => (
          <Grid 
            item 
            key={index} 
            xs={4} sm={2} 
            sx={{ textAlign: 'center', cursor: 'pointer' }}
          >
            <Avatar
              sx={{
                bgcolor: '#536878',
                width: 64,
                height: 64,
                mx: 'auto',
                mb: 1,
                transition: 'transform 0.3s, box-shadow 0.3s',
                '&:hover': {
                  transform: 'scale(1.2)',
                  boxShadow: '0 4px 20px rgba(83, 104, 120, 0.7)',
                },
                color: '#EAE0C8',
              }}
            >
              {icon}
            </Avatar>
            <Typography variant="body2" fontWeight="medium">
              {name}
            </Typography>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Skills;
