import React, { useEffect, useState } from 'react';
import { Box, Typography, Grid } from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';

const Education = () => {
  const [gpa, setGpa] = useState(0);

  // GPA animasyonu
  useEffect(() => {
    let start = 0;
    const end = 3.10;
    const duration = 3000; // ms
    const increment = end / (duration / 50);
    const timer = setInterval(() => {
      start += increment;
      if (start >= end) {
        start = end;
        clearInterval(timer);
      }
      setGpa(start.toFixed(2));
    }, 50);

    return () => clearInterval(timer);
  }, []);

  return (
    <Box
      id="education"
      sx={{
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        p: 2,
      }}
    >
      <Box
        sx={{
          bgcolor: 'white',
          p: 4,
          borderRadius: 4,
          boxShadow: 6,
          width: { xs: '100%', sm: '90%', md: '70%', lg: '60%' },
          maxWidth: 800,
          transition: 'transform 0.3s ease, box-shadow 0.3s ease',
          '&:hover': {
            transform: 'scale(1.02)',
            boxShadow: 10,
            color:'white'
          },
        }}
      >
        {/* Başlık */}
        <Typography
         variant="h4"
          sx={{
            textAlign: 'center',
            fontWeight: 'bold',
            mb: 3,
            color: '#2D336B',
             }}>
          Eğitim
        </Typography>

        
        <Grid container alignItems="center" justifyContent="space-between" >
          <Grid item sx={{ display: 'flex', alignItems: 'center' }}>
            <SchoolIcon sx={{ mr: 1, color: '#2D336B' }} />
            <Typography variant="h6">Hacettepe Üniversitesi</Typography>
          </Grid>

         <Grid container alignItems="center" justifyContent="space-between" >
            <CalendarMonthIcon sx={{ mr:1, color:'#2D336B'}}/>
            <Typography variant="body1" sx={{ fontWeight: 'medium' }}>
              2020 - 2024
            </Typography>
          </Grid>
        </Grid>

        {/* Bölüm Adı */}
        <Box sx={{ mt: 3, textAlign: 'center' }}>
          <Typography variant="body1" sx={{ fontSize: { xs: 16, sm: 18 }, color: '#555' }}>
            Bilgisayar ve Öğretim Teknolojileri Eğitimi
          </Typography>
        </Box>

        {/* GPA */}
        <Box sx={{ textAlign: 'right', mt: 5 }}>
          <Typography
            variant="body2"
            sx={{
              fontWeight: 'bold',
              fontSize: 14,
              color: '#4caf50',
            }}
          >
            GPA: 4.0 / {gpa}
          </Typography>
        </Box>


      </Box>
    </Box>
  );
};

export default Education;
