import { useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode";
import {
  Box,
  Typography,
  Paper,
  TextField,
  Button,
} from "@mui/material";
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
} from "@mui/lab";
import WorkspacesIcon from "@mui/icons-material/Workspaces";

const Experience = () => {
  const [experienceData, setExperienceData] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [newExperience, setNewExperience] = useState({
    title: "",
    descriptions: [""],
  });

  // isAdmin kontrolü
  useEffect(() => {
    const token = sessionStorage.getItem("token");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setIsAdmin(decoded.isAdmin === true);
      } catch (err) {
        console.error("Token çözümlenemedi:", err);
      }
    }
  }, []);

  // Verileri çek
  useEffect(() => {
    fetchExperience();
  }, []);

  const fetchExperience = async () => {
    try {
      const response = await fetch(
        "http://localhost:3003/experience/getText"
      );
      const data = await response.json();
      setExperienceData(data);
    } catch (error) {
      console.error("Verileri getiremedim:", error);
    }
  };
const handleAddExperience = async () => {
  const token = sessionStorage.getItem("token");
  try {
    const response = await fetch("http://localhost:3003/experience/addText", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        title: newExperience.title,
        descriptions: newExperience.descriptions.filter(
          (desc) => desc.trim() !== ""
        ),
      }),
    });

    if (response.ok) {
      const updated = await response.json();
      setExperienceData((prev) => [...prev, updated]);
      setNewExperience({ title: "", descriptions: [""] });
      setShowForm(false);
    } else {
      console.error("Ekleme başarısız:", response.statusText);
    }
  } catch (error) {
    console.error("API çağrısı sırasında hata:", error);
  }
};


  return (
    <Box id="experience" sx={{ py: 6, px: 2 }}>
      <Typography
        variant="h4"
        align="center"
        gutterBottom
        sx={{
          mb: 4,
          fontWeight: "bold",
          bgcolor: "white",
          WebkitBackgroundClip: "text",
          WebkitTextFillColor: "transparent",
          backgroundClip: "text",
          textFillColor: "transparent",
        }}
      >
        Deneyim
      </Typography>

      <Box
        sx={{
          display: "flex",
          justifyContent: "center",
        }}
      >
        <Timeline
          position="right"
          sx={{
            maxWidth: "800px",
            width: "100%",
            "& .MuiTimelineItem-root:before": {
              flex: 0,
              padding: 0,
            },
          }}
        >
          {experienceData.map((item, index) => (
            <TimelineItem key={item.id}>
              <TimelineSeparator
                sx={{
                  position: "relative",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                {index !== experienceData.length - 1 && (
                  <TimelineConnector
                    sx={{
                      position: "absolute",
                      top: 0,
                      bottom: 0,
                      left: "50%",
                      transform: "translateX(-50%)",
                      zIndex: 1,
                      bgcolor: "primary.main",
                      width: 2,
                    }}
                  />
                )}
                <TimelineDot
                  variant="outlined"
                  sx={{
                    bgcolor: "white",
                    border: "none",
                    zIndex: 2,
                  }}
                >
                  <WorkspacesIcon color="primary" />
                </TimelineDot>
              </TimelineSeparator>

              <TimelineContent
                sx={{
                  display: "flex",
                  justifyContent: "center",
                  mb: 2,
                }}
              >
                <Paper
                  elevation={3}
                  sx={{
                    p: 2,
                    color: "black",
                    background:
                      "linear-gradient(100deg, #03346E,rgb(69, 131, 207))",
                    borderRadius: 2,
                    maxWidth: "600px",
                    width: "100%",
                    "&:hover": {
                      transform: "scale(1.1)",
                      filter:
                        "drop-shadow(0 0 1px #E2E2B6) drop-shadow(0 0 5px #E2E2B6)",
                    },
                  }}
                >
                  <Typography
                    variant="h6"
                    sx={{
                      fontWeight: "bold",
                      textAlign: "center",
                      color: "white",
                      pb: 4,
                    }}
                  >
                    {item.title}
                  </Typography>
                  {item.descriptions.map((desc, i) => (
                    <Typography
                      key={i}
                      variant="body2"
                      sx={{
                        textAlign: "center",
                        color: "white",
                        p: 1,
                      }}
                    >
                      • {desc}
                    </Typography>
                  ))}
                </Paper>
              </TimelineContent>
            </TimelineItem>
          ))}
        </Timeline>
      </Box>

      {/* Admin ise ekleme butonu */}
      {isAdmin && (
        <Box
          sx={{
            display: "flex",
            justifyContent: "flex-end",
            mb: 2,
            mr: 2,
          }}
        >
          <Button
            variant="outlined"
            onClick={() => setShowForm((prev) => !prev)}
            startIcon={<WorkspacesIcon />}
          >
            + Bi Şeyler Ekle
          </Button>
        </Box>
      )}

      {/* Ekleme Formu */}
      {showForm && (
        <Box
          sx={{
            mt: 4,
            display: "flex",
            flexDirection: "column",
            gap: 2,
            maxWidth: "600px",
            margin: "auto",
          }}
        >
          <TextField
            label="Başlık"
            value={newExperience.title}
            onChange={(e) =>
              setNewExperience({ ...newExperience, title: e.target.value })
            }
          />
          {newExperience.descriptions.map((desc, index) => (
            <TextField
              key={index}
              label={`Açıklama ${index + 1}`}
              value={desc}
              onChange={(e) => {
                const updatedDescriptions = [...newExperience.descriptions];
                updatedDescriptions[index] = e.target.value;
                setNewExperience({
                  ...newExperience,
                  descriptions: updatedDescriptions,
                });
              }}
            />
          ))}
          <Button
            
            variant="outlined"
            onClick={() =>
              setNewExperience({
                ...newExperience,
                descriptions: [...newExperience.descriptions, ""],
              })
            }
          >
            + Yeni Açıklama
          </Button>
          <Button variant="contained" onClick={handleAddExperience}>
            Kaydet
          </Button>
        </Box>
      )}
    </Box>
  );
};

export default Experience;
