import React, { useEffect, useState } from "react";
import axios from "axios";
import {jwtDecode} from "jwt-decode";
import {
  Box,
  Typography,
  Paper,
  Button,
  Stack,
  TextField,
} from "@mui/material";
import {
  Timeline,
  TimelineItem,
  TimelineSeparator,
  TimelineConnector,
  TimelineContent,
  TimelineDot,
  
} from "@mui/lab";
import EditIcon from '@mui/icons-material/Edit';
import WorkspacesIcon from '@mui/icons-material/Workspaces';

const Experience = () => {
  const [experienceData, setExperienceData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Admin kontrol için
  const [isAdmin, setIsAdmin] = useState(false);

  // Form state
  const [title, setTitle] = useState("");
  const [descriptions, setDescriptions] = useState([""]);

  // Form görünürlüğü
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    // Token'ı localStorage'dan al (örnek)
const token = localStorage.getItem("token") || sessionStorage.getItem("token");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setIsAdmin(decoded.isAdmin || false); // token içinde isAdmin alanı varsa
      } catch (err) {
        setIsAdmin(false);
      }
    }

    axios.get("http://localhost:3003/experience/getText")
      .then(response => {
        const data = response.data.map(item => ({
          ...item,
          description: typeof item.description === "string" ? JSON.parse(item.description) : item.description,
        }));
        setExperienceData(data);
        setLoading(false);
      })
      .catch(() => {
        setError("Veri çekilirken hata oluştu.");
        setLoading(false);
      });
  }, []);

  const handleAddDescription = () => {
    setDescriptions([...descriptions, ""]);
  };

  const handleDescriptionChange = (index, value) => {
    const newDescriptions = [...descriptions];
    newDescriptions[index] = value;
    setDescriptions(newDescriptions);
  };

  const handleSubmit = async () => {
    if (!title.trim() || descriptions.some(desc => !desc.trim())) {
      alert("Lütfen başlık ve açıklamaları doldurun.");
      return;
    }
    try {
      const token = localStorage.getItem("token") || sessionStorage.getItem("token");
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };
      const res = await axios.post("http://localhost:3003/experience/addText", {
        title,
        description: descriptions
      }, config);
      
      // Yeni eklenen veriyi listeye ekle
      setExperienceData(prev => [...prev, { ...res.data, description: descriptions }]);
      setTitle("");
      setDescriptions([""]);
      setShowForm(false);
    } catch (err) {
      alert("Ekleme sırasında hata oluştu.");
      console.error(err);
    }
  };

  if (loading) return <Typography align="center">Yükleniyor...</Typography>;
  if (error) return <Typography color="error" align="center">{error}</Typography>;


  const handleEditClick = (item) => {
  setEditingId(item.id);
  setTitle(item.title);
  setDescriptions(item.description);
  setShowForm(true);
};

// Formu kapat
const handleCancel = () => {
  setEditingId(null);
  setTitle("");
  setDescriptions([""]);
  setShowForm(false);
};

// Güncelleme işlemi
const handleUpdate = async () => {
  if (!title.trim() || descriptions.some(desc => !desc.trim())) {
    alert("Lütfen başlık ve açıklamaları doldurun.");
    return;
  }
  try {
    const token = localStorage.getItem("token") || sessionStorage.getItem("token");
    const config = {
      headers: { Authorization: `Bearer ${token}` }
    };
    // PUT isteği, updateText endpointi varsayımı
    const res = await axios.put("http://localhost:3003/experience/updateText", {
      id: editingId,
      title,
      description: descriptions
    }, config);

    // Gelen yeni veriyi listede güncelle
    setExperienceData(prev =>
      prev.map(item => (item.id === editingId ? { ...item, title, description: descriptions } : item))
    );

    handleCancel();
  } catch (err) {
    alert("Güncelleme sırasında hata oluştu.");
    console.error(err);
  }
};

  return (
    <Box id="experience" sx={{ py: 6, px: 2 }}>
      <Typography
        variant="h4"
        align="center"
        gutterBottom
        sx={{
          mb: 4,
          fontWeight: 'bold',
          bgcolor:'white',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text',
          textFillColor: 'transparent',
        }}
      >
        Deneyim
      </Typography>

      {/* Sadece admin ise göster */}
      {isAdmin && (
        <Box sx={{ mb: 4, textAlign: "center" }}>
          {!showForm ? (
            <Button variant="contained" onClick={() => setShowForm(true)}>
              Yeni Deneyim Ekle
            </Button>
          ) : (
            <Paper sx={{ p: 3, maxWidth: 600, mx: "auto" }} elevation={3}>
              <Stack spacing={2}>
                <TextField
                  label="Başlık"
                  value={title}
                  onChange={e => setTitle(e.target.value)}
                  fullWidth
                />
                {descriptions.map((desc, i) => (
                  <TextField
                    key={i}
                    label={`Açıklama ${i + 1}`}
                    value={desc}
                    onChange={e => handleDescriptionChange(i, e.target.value)}
                    fullWidth
                    multiline
                    minRows={2}
                  />
                ))}
                <Button variant="outlined" onClick={handleAddDescription}>
                  Açıklama Satırı Ekle
                </Button>
                <Stack direction="row" spacing={2} justifyContent="center">
                  <Button variant="contained" onClick={handleSubmit}>
                    Kaydet
                  </Button>
                  <Button variant="text" onClick={() => setShowForm(false)}>
                    İptal
                  </Button>
                </Stack>
              </Stack>
            </Paper>
          )}
        </Box>
      )}

      {/* Timeline kısmı */}
      <Box sx={{ display: "flex", justifyContent: "center" }}>
        <Timeline
          position="right"
          sx={{
            maxWidth: "800px",
            width: "100%",
            "& .MuiTimelineItem-root:before": {
              flex: 0,
              padding: 0,
            },
          }}
        >
          {experienceData.map((item, index) => (
            <TimelineItem key={index}>
              <TimelineSeparator
                sx={{
                  position: "relative",
                  display: "flex",
                  flexDirection: "column",
                  alignItems: "center",
                }}
              >
                {index !== experienceData.length - 1 && (
                  <TimelineConnector
                    sx={{
                      position: "absolute",
                      top: 0,
                      bottom: 0,
                      left: "50%",
                      transform: "translateX(-50%)",
                      zIndex: 1,
                      bgcolor: "primary.main",
                      width: 2,
                    }}
                  />
                )}
                <TimelineDot
                  variant="outlined"
                  sx={{
                    bgcolor: "white",
                    border: "none",
                    zIndex: 2,
                  }}
                >
                  <WorkspacesIcon color="primary" />
                </TimelineDot>
              </TimelineSeparator>

              <TimelineContent
                sx={{
                  display: "flex",
                  justifyContent: "center",
                  mb: 2,
                }}
              >
                <Paper
                  elevation={3}
                  sx={{
                    p: 2,
                    color: "black",
                    background:'linear-gradient(100deg, #03346E,rgb(69, 131, 207))',
                    borderRadius: 2,
                    maxWidth: "600px",
                    width: "100%",
                    "&:hover": {
                      transform: "scale(1.1)",
                      filter:
                        "drop-shadow(0 0 1px #E2E2B6) drop-shadow(0 0 5px #E2E2B6)",
                    },
                  }}
                > 
                  <Typography
                    variant="h6"
                    sx={{
                      fontWeight: "bold",
                      textAlign: "center",
                      color:'white',
                      pb: 4,
                    }}
                  >
                    {item.title}
                  </Typography>
                  {item.description.map((desc, i) => (
                    <Typography
                      key={i}
                      variant="body2"
                      sx={{
                        textAlign: "center",
                        color:'white',
                        p: 1,
                      }}
                    >
                      • {desc}
                    </Typography>
                  ))}
                </Paper>
              </TimelineContent>
            </TimelineItem>
          ))}
        </Timeline>
      </Box>
    </Box>
  );
};

export default Experience;
