import React from 'react';
import { Box, Typography, Avatar, IconButton } from '@mui/material';
import MailOutlineIcon from '@mui/icons-material/MailOutline';
import ArrowForwardIosIcon from '@mui/icons-material/ArrowForwardIos';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import GitHubIcon from '@mui/icons-material/GitHub';
import InstagramIcon from '@mui/icons-material/Instagram';
import aboutData from '../data/about';



const neonIconStyle = {
  color: 'white',
  transition: 'transform 0.3s ease, filter 0.3s ease',
  filter: 'drop-shadow(0 0 6px rgb(0, 0, 0)) drop-shadow(0 0 10px rgb(61, 14, 148))',
  '&:hover': {
    transform: 'scale(1.3)',
    filter: 'drop-shadow(0 0 12pxrgb(255, 255, 255)) drop-shadow(0 0 20px #ffbbff)',
  },
};



const About = () => {
  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        p: 2,
        overflowY: 'auto',
      }}
    >
      <Box
        sx={{
          display: 'flex',
          flexDirection: { xs: 'column', md: 'row' },
          width: { xs: '100%', sm: '90%', md: '70%' },
          boxShadow: 6,
          borderRadius: 4,
          overflow: 'hidden',
        }}
      >
        {/* Sol Kısım */}
        <Box
          sx={{
            width: { xs: '100%', md: '30%' },
            background: 'linear-gradient(to bottom,rgb(27, 186, 214),rgb(12, 150, 192),rgb(60, 48, 218))',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            p: 3,
          }}
        >
          <Avatar
            src="asd.jpg"
            alt="Yakup Şimşek"
            sx={{ width: 240, height: 240, mb: 2, border: '3px solid white' }}
          />
          <Typography variant="h4" sx={{ color: 'white', mb: 1, textAlign: 'center' }}>
            Yakup Şimşek
          </Typography>
          <Typography variant="h6" sx={{ color: 'white', mb: 2, textAlign: 'center' }}>
            Eğitim Teknoloğu
          </Typography>
          <Box>
              <IconButton
                    sx={{
                      transition: 'all 0.3s ease',
                      '&:hover svg': {
                        transform: 'scale(1.3)',
                        filter: 'drop-shadow(0 0 32px rgb(24, 1, 126)) drop-shadow(0 0 20px rgb(20, 11, 148))',
                      },
                    }}
                  >
                    <LinkedInIcon sx={{ fontSize: 30, ...neonIconStyle }} />
              </IconButton>

              <IconButton
                    sx={{
                      transition: 'all 0.3s ease',
                      '&:hover svg': {
                        transform: 'scale(1.3)',
                        filter: 'drop-shadow(0 0 12px #eeaaff) drop-shadow(0 0 20px #ffbbff)',
                      },
                    }}
                  >
                    <GitHubIcon sx={{ fontSize: 30, ...neonIconStyle }} />
              </IconButton>

              <IconButton
                    sx={{
                      transition: 'all 0.3s ease',
                      '&:hover svg': {
                        transform: 'scale(1.3)',
                        filter: 'drop-shadow(0 0 12px #eeaaff) drop-shadow(0 0 20px #ffbbff)',
                      },
                    }}
                  >
                    <InstagramIcon sx={{ fontSize: 30, ...neonIconStyle }} />
              </IconButton>


          </Box>
        </Box>

        {/* Sağ Kısım */}
        <Box
          sx={{
            width: { xs: '100%', md: '70%' },
            bgcolor: 'white',
            p: 4,
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between',
            alignItems:'center',
            borderTopLeftRadius: { xs: 0, md: 40 },
            borderBottomLeftRadius: { xs: 0, md: 40 },
            ml: { xs: 0, md: '-30px' },
            zIndex: 1,
          }}
        >
           <Typography
              variant="h4"
              sx={{
                color: '#001f4d',
                fontWeight: 'bold',
                mb: 3,
                textAlign: 'center',
              }}
            >
              Hakkımda
           </Typography>
          {aboutData.map((item, index) => (
            <Typography key={index} variant="body1" sx={{ color: '#333', mb: 2, textAlign:'center' }}>
              • {item}
            </Typography>
          ))}

          <Box sx={{ display: 'flex', alignItems: 'center', mt: 2, flexWrap: 'wrap' }}>
            <MailOutlineIcon sx={{ color: '#red', mr: 1 }} />
            <Box
              onClick={() => window.open('mailto:yakupsimseke@gmail.com')}
              sx={{
                display: 'flex',
                alignItems: 'center',
                gap: 1,
                px: 2,
                py: 1,
                bgcolor: 'white',
                borderRadius: 20,
                cursor: 'pointer',
                border: '2px solid #7886C7',
                color: 'black',
                transition: '0.3s',
                '&:hover': {
                  bgcolor: '#001f4d',
                  color: 'white',
                },
                mt: { xs: 1, md: 0 },
              }}
            >
              <Typography
               variant="body1"
               sx={{
                 fontWeight: '20px'
                  }}>
                Benimle İletişime Geçin
              </Typography>
              <ArrowForwardIosIcon sx={{ fontSize: 16 }} />
            </Box>
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default About;
